import React, { useState, useEffect } from 'react';
import '../styles/LeaderboardWidget.css';

const LeaderboardWidget = ({ 
    showAllLeaderboards = false, 
    token = null, 
    compact = false,
    leaderboardId = null,
    maxEntries = 10
}) => {
    const [leaderboards, setLeaderboards] = useState([]);
    const [selectedLeaderboard, setSelectedLeaderboard] = useState(null);
    const [leaderboardData, setLeaderboardData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [userPosition, setUserPosition] = useState(null);

    useEffect(() => {
        if (token) {
            fetchLeaderboards();
        }
    }, [token]);

    useEffect(() => {
        if (selectedLeaderboard && token) {
            fetchLeaderboardData(selectedLeaderboard.id);
        }
    }, [selectedLeaderboard, token]);

    const fetchLeaderboards = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5000/api/gamification/leaderboards', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const data = await response.json();
                setLeaderboards(data.data || []);
                
                // Auto-select first leaderboard or specified one
                if (data.data && data.data.length > 0) {
                    const defaultBoard = leaderboardId 
                        ? data.data.find(lb => lb.id === leaderboardId) 
                        : data.data[0];
                    if (defaultBoard) {
                        setSelectedLeaderboard(defaultBoard);
                    }
                }
            } else {
                setError('Failed to load leaderboards');
            }
        } catch (error) {
            console.error('Error fetching leaderboards:', error);
            setError('Failed to connect to server');
        } finally {
            setLoading(false);
        }
    };

    const fetchLeaderboardData = async (leaderboardId) => {
        setLoading(true);
        try {
            const response = await fetch(
                `http://localhost:5000/api/gamification/leaderboards/${leaderboardId}?limit=${maxEntries}`,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                }
            );

            if (response.ok) {
                const data = await response.json();
                setLeaderboardData(data.data.entries || []);
                setUserPosition(data.data.userPosition || null);
            } else {
                setError('Failed to load leaderboard data');
            }
        } catch (error) {
            console.error('Error fetching leaderboard data:', error);
            setError('Failed to connect to server');
        } finally {
            setLoading(false);
        }
    };

    const getRankIcon = (rank) => {
        switch (rank) {
            case 1: return 'ðŸ¥‡';
            case 2: return 'ðŸ¥ˆ';
            case 3: return 'ðŸ¥‰';
            default: return `#${rank}`;
        }
    };

    const getRankColor = (rank) => {
        switch (rank) {
            case 1: return '#FFD700';
            case 2: return '#C0C0C0';
            case 3: return '#CD7F32';
            default: return '#666';
        }
    };

    const formatValue = (value, type) => {
        if (type === 'xp') {
            if (value >= 1000000) return `${(value / 1000000).toFixed(1)}M`;
            if (value >= 1000) return `${(value / 1000).toFixed(1)}K`;
            return value.toString();
        }
        return value;
    };

    const getLeaderboardIcon = (type) => {
        const icons = {
            xp: 'â­',
            badges: 'ðŸ…',
            achievements: 'ðŸ†',
            games: 'ðŸŽ®',
            streaks: 'ðŸ”¥'
        };
        return icons[type] || 'ðŸ“Š';
    };

    const getPeriodLabel = (period) => {
        const labels = {
            daily: 'Today',
            weekly: 'This Week',
            monthly: 'This Month',
            all_time: 'All Time'
        };
        return labels[period] || period;
    };

    if (loading && leaderboards.length === 0) {
        return (
            <div className="leaderboard-widget">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Loading leaderboards...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="leaderboard-widget">
                <div className="error-container">
                    <p className="error-message">{error}</p>
                    <button onClick={fetchLeaderboards} className="retry-btn">
                        Try Again
                    </button>
                </div>
            </div>
        );
    }

    if (compact && selectedLeaderboard) {
        return (
            <div className="leaderboard-widget compact">
                <div className="leaderboard-header">
                    <h3>
                        {getLeaderboardIcon(selectedLeaderboard.type)} {selectedLeaderboard.name}
                    </h3>
                    <span className="period-label">{getPeriodLabel(selectedLeaderboard.period)}</span>
                </div>
                
                <div className="leaderboard-list">
                    {leaderboardData.slice(0, 5).map((entry, index) => (
                        <div key={entry.userId} className="leaderboard-entry compact">
                            <div className="rank" style={{ color: getRankColor(entry.rank) }}>
                                {getRankIcon(entry.rank)}
                            </div>
                            <div className="user-info">
                                <span className="username">{entry.username}</span>
                            </div>
                            <div className="score">
                                {formatValue(entry.value, selectedLeaderboard.type)}
                            </div>
                        </div>
                    ))}
                </div>

                {userPosition && (
                    <div className="user-position">
                        <span>Your Position: #{userPosition.rank}</span>
                    </div>
                )}
            </div>
        );
    }

    return (
        <div className="leaderboard-widget">
            {showAllLeaderboards && (
                <div className="leaderboard-tabs">
                    {leaderboards.map((leaderboard) => (
                        <button
                            key={leaderboard.id}
                            className={`tab-btn ${selectedLeaderboard?.id === leaderboard.id ? 'active' : ''}`}
                            onClick={() => setSelectedLeaderboard(leaderboard)}
                        >
                            {getLeaderboardIcon(leaderboard.type)} {leaderboard.name}
                        </button>
                    ))}
                </div>
            )}

            {selectedLeaderboard && (
                <div className="leaderboard-content">
                    <div className="leaderboard-header">
                        <div className="header-info">
                            <h2>
                                {getLeaderboardIcon(selectedLeaderboard.type)} {selectedLeaderboard.name}
                            </h2>
                            <p className="leaderboard-description">{selectedLeaderboard.description}</p>
                        </div>
                        <div className="header-meta">
                            <span className="period-badge">{getPeriodLabel(selectedLeaderboard.period)}</span>
                            <span className="entries-count">{leaderboardData.length} entries</span>
                        </div>
                    </div>

                    {loading ? (
                        <div className="loading-container">
                            <div className="loading-spinner"></div>
                            <p>Loading leaderboard...</p>
                        </div>
                    ) : (
                        <>
                            <div className="leaderboard-table">
                                <div className="table-header">
                                    <div className="header-rank">Rank</div>
                                    <div className="header-user">User</div>
                                    <div className="header-score">Score</div>
                                    <div className="header-change">Change</div>
                                </div>
                                
                                <div className="table-body">
                                    {leaderboardData.map((entry, index) => (
                                        <div 
                                            key={entry.userId} 
                                            className={`leaderboard-entry ${entry.isCurrentUser ? 'current-user' : ''}`}
                                        >
                                            <div className="rank" style={{ color: getRankColor(entry.rank) }}>
                                                <span className="rank-icon">{getRankIcon(entry.rank)}</span>
                                            </div>
                                            <div className="user-info">
                                                <div className="user-details">
                                                    <span className="username">{entry.username}</span>
                                                    {entry.isCurrentUser && (
                                                        <span className="you-badge">You</span>
                                                    )}
                                                </div>
                                                {entry.userLevel && (
                                                    <span className="user-level">Level {entry.userLevel}</span>
                                                )}
                                            </div>
                                            <div className="score">
                                                <span className="score-value">
                                                    {formatValue(entry.value, selectedLeaderboard.type)}
                                                </span>
                                                {selectedLeaderboard.type === 'xp' && (
                                                    <span className="score-unit">XP</span>
                                                )}
                                            </div>
                                            <div className="change">
                                                {entry.change !== undefined && (
                                                    <span className={`change-indicator ${entry.change > 0 ? 'up' : entry.change < 0 ? 'down' : 'same'}`}>
                                                        {entry.change > 0 ? 'â†—' : entry.change < 0 ? 'â†˜' : 'â€”'}
                                                        {entry.change !== 0 && Math.abs(entry.change)}
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {userPosition && !userPosition.inTopList && (
                                <div className="user-position-card">
                                    <div className="position-header">
                                        <h4>Your Position</h4>
                                    </div>
                                    <div className="position-content">
                                        <div className="rank" style={{ color: getRankColor(userPosition.rank) }}>
                                            #{userPosition.rank}
                                        </div>
                                        <div className="user-info">
                                            <span className="username">{userPosition.username}</span>
                                        </div>
                                        <div className="score">
                                            {formatValue(userPosition.value, selectedLeaderboard.type)}
                                        </div>
                                    </div>
                                </div>
                            )}

                            <div className="leaderboard-footer">
                                <p className="update-info">
                                    Last updated: {new Date(selectedLeaderboard.lastUpdated || Date.now()).toLocaleString()}
                                </p>
                                <button 
                                    onClick={() => fetchLeaderboardData(selectedLeaderboard.id)}
                                    className="refresh-btn"
                                >
                                    ðŸ”„ Refresh
                                </button>
                            </div>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default LeaderboardWidget;
