import React, { useState, useEffect } from 'react';
import '../styles/AchievementDisplay.css';

const AchievementDisplay = ({ 
    achievement, 
    userAchievement = null, 
    compact = false, 
    showAllAchievements = false,
    token = null 
}) => {
    const [achievements, setAchievements] = useState([]);
    const [loading, setLoading] = useState(false);
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        if (showAllAchievements && token) {
            fetchAchievements();
        }
    }, [showAllAchievements, token, filter]);

    const fetchAchievements = async () => {
        setLoading(true);
        try {
            let url = 'http://localhost:5000/api/gamification/achievements';
            if (filter !== 'all') {
                url += `?status=${filter}`;
            }

            const response = await fetch(url, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const data = await response.json();
                setAchievements(data.data || []);
            }
        } catch (error) {
            console.error('Error fetching achievements:', error);
        } finally {
            setLoading(false);
        }
    };

    const getRarityColor = (rarity) => {
        const colors = {
            common: '#8B8680',
            rare: '#2196F3',
            epic: '#9C27B0',
            legendary: '#FF9800'
        };
        return colors[rarity] || colors.common;
    };

    const getRarityName = (rarity) => {
        const names = {
            common: 'Common',
            rare: 'Rare',
            epic: 'Epic',
            legendary: 'Legendary'
        };
        return names[rarity] || 'Common';
    };

    const formatProgress = (current, target) => {
        if (target <= 1) return `${current >= target ? 'Complete' : 'Incomplete'}`;
        return `${current}/${target}`;
    };

    const getProgressPercentage = (current, target) => {
        return Math.min((current / target) * 100, 100);
    };

    const isCompleted = (userAch) => {
        return userAch?.status === 'completed' || userAch?.completedAt;
    };

    const formatDate = (dateString) => {
        return new Date(dateString).toLocaleDateString();
    };

    // Single achievement display
    if (!showAllAchievements && achievement) {
        const completed = isCompleted(userAchievement);
        const progress = userAchievement?.progress || { current: 0, target: 1 };

        if (compact) {
            return (
                <div className={`achievement-compact ${completed ? 'completed' : 'incomplete'}`}>
                    <div className="achievement-icon">{achievement.icon}</div>
                    <div className="achievement-info">
                        <h4 className="achievement-name">{achievement.name}</h4>
                        <p className="achievement-description">{achievement.description}</p>
                        {!completed && (
                            <div className="progress-bar">
                                <div 
                                    className="progress-fill" 
                                    style={{ 
                                        width: `${getProgressPercentage(progress.current, progress.target)}%`,
                                        backgroundColor: getRarityColor(achievement.rarity)
                                    }}
                                ></div>
                            </div>
                        )}
                    </div>
                    <div className="achievement-status">
                        {completed ? (
                            <span className="completed-badge">‚úì</span>
                        ) : (
                            <span className="progress-text">{formatProgress(progress.current, progress.target)}</span>
                        )}
                    </div>
                </div>
            );
        }

        return (
            <div className={`achievement-card ${completed ? 'completed' : 'incomplete'}`}>
                <div className="achievement-header">
                    <div className="achievement-icon-large">{achievement.icon}</div>
                    <div className="achievement-meta">
                        <h3 className="achievement-title">{achievement.name}</h3>
                        <p className="achievement-desc">{achievement.description}</p>
                        <span 
                            className="achievement-rarity"
                            style={{ color: getRarityColor(achievement.rarity) }}
                        >
                            {getRarityName(achievement.rarity)}
                        </span>
                    </div>
                </div>

                <div className="achievement-details">
                    {achievement.longDescription && (
                        <p className="long-description">{achievement.longDescription}</p>
                    )}

                    <div className="achievement-rewards">
                        {achievement.xpReward > 0 && (
                            <div className="reward-item">
                                <span className="reward-icon">‚≠ê</span>
                                <span className="reward-text">+{achievement.xpReward} XP</span>
                            </div>
                        )}
                        {achievement.points > 0 && (
                            <div className="reward-item">
                                <span className="reward-icon">üíé</span>
                                <span className="reward-text">+{achievement.points} Points</span>
                            </div>
                        )}
                    </div>

                    {!completed ? (
                        <div className="achievement-progress">
                            <div className="progress-header">
                                <span className="progress-label">Progress</span>
                                <span className="progress-numbers">{formatProgress(progress.current, progress.target)}</span>
                            </div>
                            <div className="progress-bar-container">
                                <div className="progress-bar">
                                    <div 
                                        className="progress-fill" 
                                        style={{ 
                                            width: `${getProgressPercentage(progress.current, progress.target)}%`,
                                            backgroundColor: getRarityColor(achievement.rarity)
                                        }}
                                    ></div>
                                </div>
                                <span className="progress-percentage">
                                    {Math.round(getProgressPercentage(progress.current, progress.target))}%
                                </span>
                            </div>
                        </div>
                    ) : (
                        <div className="achievement-completed">
                            <div className="completed-info">
                                <span className="completed-icon">üéâ</span>
                                <span className="completed-text">
                                    Completed on {userAchievement?.completedAt ? formatDate(userAchievement.completedAt) : 'Unknown'}
                                </span>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        );
    }

    // All achievements display
    if (showAllAchievements) {
        return (
            <div className="achievements-container">
                <div className="achievements-header">
                    <h2>üèÜ Your Achievements</h2>
                    <div className="achievements-filters">
                        <button 
                            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
                            onClick={() => setFilter('all')}
                        >
                            All
                        </button>
                        <button 
                            className={`filter-btn ${filter === 'completed' ? 'active' : ''}`}
                            onClick={() => setFilter('completed')}
                        >
                            Completed
                        </button>
                        <button 
                            className={`filter-btn ${filter === 'in_progress' ? 'active' : ''}`}
                            onClick={() => setFilter('in_progress')}
                        >
                            In Progress
                        </button>
                        <button 
                            className={`filter-btn ${filter === 'locked' ? 'active' : ''}`}
                            onClick={() => setFilter('locked')}
                        >
                            Locked
                        </button>
                    </div>
                </div>

                {loading ? (
                    <div className="achievements-loading">
                        <div className="loading-spinner"></div>
                        <p>Loading achievements...</p>
                    </div>
                ) : (
                    <div className="achievements-grid">
                        {achievements.length === 0 ? (
                            <div className="no-achievements">
                                <h3>No achievements found</h3>
                                <p>Start playing games to unlock achievements!</p>
                            </div>
                        ) : (
                            achievements.map((userAch, index) => (
                                <AchievementDisplay
                                    key={userAch.achievement?.id || index}
                                    achievement={userAch.achievement}
                                    userAchievement={userAch}
                                    compact={false}
                                />
                            ))
                        )}
                    </div>
                )}
            </div>
        );
    }

    return null;
};

export default AchievementDisplay;
