import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Notifications.css';

function Notifications() {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchNotifications = async () => {
    try {
      const res = await axios.get('/api/notifications', { withCredentials: true });
      setNotifications(res.data);
    } catch (err) {
      console.error('Error fetching notifications:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
  }, []);

  const markAsRead = async (id) => {
    try {
      await axios.put(`/api/notifications/read/${id}`, {}, { withCredentials: true });
      setNotifications(notifications.map(n => n.id === id ? { ...n, is_read: 1 } : n));
    } catch (err) {
      console.error('Error marking notification as read:', err);
    }
  };

  const markAllAsRead = async () => {
    try {
      await axios.put('/api/notifications/read-all', {}, { withCredentials: true });
      setNotifications(notifications.map(n => ({ ...n, is_read: 1 })));  
    } catch (err) {
      console.error('Error marking all notifications as read:', err);
    }
  };

  if (loading) return <p>Chargement des notifications...</p>;

  return (
    <div className="notifications-page">
      <h2>Mes Notifications</h2>
      {notifications.length > 0 ? (
        <>  
          <button className="mark-all-btn" onClick={markAllAsRead}>
            Tout marquer comme lu
          </button>
          <ul className="notifications-list">
            {notifications.map(n => (
              <li key={n.id} className={`notification-item ${n.is_read ? '' : 'unread'}`}>
                <div>
                  <strong>{n.title}</strong>
                  <p>{n.content}</p>
                  <small>{new Date(n.created_at).toLocaleString()}</small>
                </div>
                {!n.is_read && (
                  <button className="read-btn" onClick={() => markAsRead(n.id)}>
                    Marquer comme lu
                  </button>
                )}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>Aucune notification.</p>
      )}
    </div>
  );
}

export default Notifications;
