import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import '../styles/NotificationToast.css';

const NotificationToast = () => {
    const { token } = useContext(AuthContext);
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        if (token) {
            // Listen for gamification events
            const eventSource = new EventSource(`http://localhost:5000/api/gamification/events/stream?token=${token}`);
            
            eventSource.onmessage = (event) => {
                try {
                    const notification = JSON.parse(event.data);
                    showNotification(notification);
                } catch (error) {
                    console.error('Error parsing notification:', error);
                }
            };

            eventSource.onerror = (error) => {
                console.error('EventSource error:', error);
                eventSource.close();
            };

            return () => {
                eventSource.close();
            };
        }
    }, [token]);

    const showNotification = (notification) => {
        const id = Date.now();
        const newNotification = { ...notification, id };
        
        setNotifications(prev => [...prev, newNotification]);

        // Auto-remove after 5 seconds
        setTimeout(() => {
            removeNotification(id);
        }, 5000);
    };

    const removeNotification = (id) => {
        setNotifications(prev => prev.filter(n => n.id !== id));
    };

    const getNotificationIcon = (type) => {
        const icons = {
            level_up: 'ðŸŽ‰',
            badge_earned: 'ðŸ…',
            achievement_unlocked: 'ðŸ†',
            xp_gained: 'â­',
            streak_milestone: 'ðŸ”¥',
            leaderboard_rank: 'ðŸ¥‡'
        };
        return icons[type] || 'ðŸŽŠ';
    };

    const getNotificationColor = (type) => {
        const colors = {
            level_up: '#28a745',
            badge_earned: '#ffc107',
            achievement_unlocked: '#007bff',
            xp_gained: '#17a2b8',
            streak_milestone: '#dc3545',
            leaderboard_rank: '#6f42c1'
        };
        return colors[type] || '#6c757d';
    };

    return (
        <div className="notification-container">
            {notifications.map((notification) => (
                <div 
                    key={notification.id}
                    className={`notification-toast ${notification.priority || 'medium'}`}
                    style={{ borderLeftColor: getNotificationColor(notification.type) }}
                >
                    <div className="notification-content">
                        <div className="notification-header">
                            <span className="notification-icon">
                                {getNotificationIcon(notification.type)}
                            </span>
                            <span className="notification-title">
                                {notification.title}
                            </span>
                            <button 
                                className="notification-close"
                                onClick={() => removeNotification(notification.id)}
                            >
                                Ã—
                            </button>
                        </div>
                        <div className="notification-message">
                            {notification.message}
                        </div>
                        {notification.metadata && (
                            <div className="notification-metadata">
                                {notification.metadata.badgeId && (
                                    <span className="metadata-badge">
                                        Badge: {notification.metadata.badgeId}
                                    </span>
                                )}
                                {notification.metadata.achievementId && (
                                    <span className="metadata-achievement">
                                        Achievement: {notification.metadata.achievementId}
                                    </span>
                                )}
                                {notification.metadata.xpGained && (
                                    <span className="metadata-xp">
                                        +{notification.metadata.xpGained} XP
                                    </span>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            ))}
        </div>
    );
};

// Hook for manually triggering notifications
export const useNotification = () => {
    const showSuccess = (title, message) => {
        // This would integrate with the toast system
        console.log('Success:', title, message);
    };

    const showError = (title, message) => {
        console.log('Error:', title, message);
    };

    const showInfo = (title, message) => {
        console.log('Info:', title, message);
    };

    return { showSuccess, showError, showInfo };
};

export default NotificationToast;
