import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/NotificationToast.css';

const NotificationToast = () => {
    const { user, token } = useAuth();
    const [notifications, setNotifications] = useState([]);

    useEffect(() => {
        if (token) {
            // Listen for gamification events
            const eventSource = new EventSource(`http://localhost:5000/api/gamification/events/stream?token=${token}`);
            
            eventSource.onmessage = (event) => {
                try {
                    const data = JSON.parse(event.data);
                    console.log('Received gamification event:', data);
                    
                    // Create notification based on event type
                    let notification = null;
                    switch (data.type) {
                        case 'xp_gained':
                            notification = {
                                id: Date.now(),
                                type: 'success',
                                title: 'XP Gagn√©!',
                                message: `+${data.amount} XP`,
                                duration: 3000
                            };
                            break;
                        case 'badge_earned':
                            notification = {
                                id: Date.now(),
                                type: 'achievement',
                                title: 'Nouveau Badge!',
                                message: data.badge.name,
                                duration: 5000
                            };
                            break;
                        case 'achievement_unlocked':
                            notification = {
                                id: Date.now(),
                                type: 'achievement',
                                title: 'Succ√®s D√©bloqu√©!',
                                message: data.achievement.name,
                                duration: 5000
                            };
                            break;
                        case 'level_up':
                            notification = {
                                id: Date.now(),
                                type: 'levelup',
                                title: 'Niveau Sup√©rieur!',
                                message: `Niveau ${data.newLevel}`,
                                duration: 4000
                            };
                            break;
                        default:
                            break;
                    }
                    
                    if (notification) {
                        setNotifications(prev => [...prev, notification]);
                        
                        // Auto-remove notification
                        setTimeout(() => {
                            setNotifications(prev => prev.filter(n => n.id !== notification.id));
                        }, notification.duration);
                    }
                } catch (error) {
                    console.error('Error parsing gamification event:', error);
                }
            };
            
            eventSource.onerror = (error) => {
                console.error('EventSource error:', error);
                eventSource.close();
            };
            
            return () => {
                eventSource.close();
            };
        }
    }, [token]);

    const removeNotification = (id) => {
        setNotifications(prev => prev.filter(n => n.id !== id));
    };

    if (!user || notifications.length === 0) {
        return null;
    }

    return (
        <div className="notification-container">
            {notifications.map(notification => (
                <div
                    key={notification.id}
                    className={`notification notification-${notification.type}`}
                    onClick={() => removeNotification(notification.id)}
                >
                    <div className="notification-header">
                        <span className="notification-icon">
                            {notification.type === 'success' && 'üéâ'}
                            {notification.type === 'achievement' && 'üèÜ'}
                            {notification.type === 'levelup' && '‚≠ê'}
                        </span>
                        <h4 className="notification-title">{notification.title}</h4>
                        <button 
                            className="notification-close"
                            onClick={(e) => {
                                e.stopPropagation();
                                removeNotification(notification.id);
                            }}
                        >
                            √ó
                        </button>
                    </div>
                    <p className="notification-message">{notification.message}</p>
                </div>
            ))}
        </div>
    );
};

export default NotificationToast;
