import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/AnalyticsDashboard.css';

const AnalyticsDashboard = () => {
    const { user, token } = useAuth();
    const [analyticsData, setAnalyticsData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [timeRange, setTimeRange] = useState('week');
    const [selectedMetric, setSelectedMetric] = useState('xp');

    useEffect(() => {
        if (token) {
            fetchAnalyticsData();
        }
    }, [token, timeRange]);

    const fetchAnalyticsData = async () => {
        setLoading(true);
        try {
            const response = await fetch(
                `http://localhost:5000/api/gamification/analytics?period=${timeRange}`,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                }
            );

            if (response.ok) {
                const data = await response.json();
                setAnalyticsData(data.data);
            } else {
                setError('Failed to load analytics data');
            }
        } catch (error) {
            console.error('Error fetching analytics:', error);
            setError('Failed to connect to server');
        } finally {
            setLoading(false);
        }
    };

    const formatNumber = (num) => {
        if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
        if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
        return num.toString();
    };

    const getPercentageChange = (current, previous) => {
        if (!previous || previous === 0) return 0;
        return ((current - previous) / previous * 100).toFixed(1);
    };

    const renderMetricCard = (title, value, previousValue, icon, color, unit = '') => {
        const change = getPercentageChange(value, previousValue);
        const isPositive = change >= 0;

        return (
            <div className="metric-card" style={{ borderTopColor: color }}>
                <div className="metric-header">
                    <div className="metric-icon" style={{ backgroundColor: color }}>
                        {icon}
                    </div>
                    <div className="metric-info">
                        <h3 className="metric-title">{title}</h3>
                        <div className="metric-value">
                            {formatNumber(value)}{unit}
                        </div>
                    </div>
                </div>
                <div className="metric-change">
                    <span className={`change-indicator ${isPositive ? 'positive' : 'negative'}`}>
                        {isPositive ? '↗' : '↘'} {Math.abs(change)}%
                    </span>
                    <span className="change-text">vs last {timeRange}</span>
                </div>
            </div>
        );
    };

    const renderChart = () => {
        if (!analyticsData?.chartData) return null;

        const chartData = analyticsData.chartData[selectedMetric] || [];
        const maxValue = Math.max(...chartData.map(d => d.value));

        return (
            <div className="chart-container">
                <div className="chart-header">
                    <h3>Performance Trend</h3>
                    <div className="metric-selector">
                        <button 
                            className={`metric-btn ${selectedMetric === 'xp' ? 'active' : ''}`}
                            onClick={() => setSelectedMetric('xp')}
                        >
                            XP
                        </button>
                        <button 
                            className={`metric-btn ${selectedMetric === 'games' ? 'active' : ''}`}
                            onClick={() => setSelectedMetric('games')}
                        >
                            Games
                        </button>
                        <button 
                            className={`metric-btn ${selectedMetric === 'badges' ? 'active' : ''}`}
                            onClick={() => setSelectedMetric('badges')}
                        >
                            Badges
                        </button>
                    </div>
                </div>
                <div className="chart-area">
                    {chartData.map((point, index) => (
                        <div key={index} className="chart-bar">
                            <div 
                                className="bar"
                                style={{ 
                                    height: `${(point.value / maxValue) * 100}%`,
                                    backgroundColor: '#007bff'
                                }}
                            ></div>
                            <span className="bar-label">{point.label}</span>
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    const renderAchievementProgress = () => {
        if (!analyticsData?.achievements) return null;

        return (
            <div className="achievement-progress">
                <h3>Achievement Progress</h3>
                <div className="achievement-stats">
                    <div className="achievement-stat">
                        <div className="stat-circle completed">
                            <span>{analyticsData.achievements.completed}</span>
                        </div>
                        <span className="stat-label">Completed</span>
                    </div>
                    <div className="achievement-stat">
                        <div className="stat-circle in-progress">
                            <span>{analyticsData.achievements.inProgress}</span>
                        </div>
                        <span className="stat-label">In Progress</span>
                    </div>
                    <div className="achievement-stat">
                        <div className="stat-circle locked">
                            <span>{analyticsData.achievements.locked}</span>
                        </div>
                        <span className="stat-label">Locked</span>
                    </div>
                </div>
                <div className="achievement-breakdown">
                    {analyticsData.achievements.byCategory?.map((cat, index) => (
                        <div key={index} className="category-progress">
                            <div className="category-header">
                                <span className="category-name">{cat.category}</span>
                                <span className="category-count">{cat.completed}/{cat.total}</span>
                            </div>
                            <div className="progress-bar">
                                <div 
                                    className="progress-fill"
                                    style={{ 
                                        width: `${(cat.completed / cat.total) * 100}%`,
                                        backgroundColor: getCategoryColor(cat.category)
                                    }}
                                ></div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        );
    };

    const getCategoryColor = (category) => {
        const colors = {
            learning: '#28a745',
            gaming: '#007bff',
            social: '#17a2b8',
            streak: '#dc3545',
            milestone: '#ffc107',
            special: '#6f42c1'
        };
        return colors[category] || '#6c757d';
    };

    const renderActivityHeatmap = () => {
        if (!analyticsData?.activity) return null;

        const days = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
        const hours = Array.from({ length: 24 }, (_, i) => i);

        return (
            <div className="activity-heatmap">
                <h3>Activity Heatmap</h3>
                <div className="heatmap-container">
                    <div className="heatmap-hours">
                        {hours.map(hour => (
                            <div key={hour} className="hour-label">
                                {hour.toString().padStart(2, '0')}
                            </div>
                        ))}
                    </div>
                    <div className="heatmap-grid">
                        {days.map(day => (
                            <div key={day} className="heatmap-row">
                                <div className="day-label">{day}</div>
                                <div className="day-hours">
                                    {hours.map(hour => {
                                        const activity = analyticsData.activity.find(
                                            a => a.day === day && a.hour === hour
                                        );
                                        const intensity = activity ? activity.intensity : 0;
                                        return (
                                            <div 
                                                key={hour} 
                                                className="activity-cell"
                                                style={{ 
                                                    backgroundColor: `rgba(0, 123, 255, ${intensity / 100})`,
                                                    opacity: Math.max(0.1, intensity / 100)
                                                }}
                                                title={`${day} ${hour}:00 - ${activity?.count || 0} activities`}
                                            ></div>
                                        );
                                    })}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        );
    };

    if (loading) {
        return (
            <div className="analytics-dashboard">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Loading analytics...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="analytics-dashboard">
                <div className="error-container">
                    <h2>⚠️ Error</h2>
                    <p>{error}</p>
                    <button onClick={fetchAnalyticsData} className="retry-btn">
                        Try Again
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="analytics-dashboard">
            <div className="dashboard-header">
                <div className="header-content">
                    <h1>📊 Analytics Dashboard</h1>
                    <p>Track your learning progress and achievements</p>
                </div>
                <div className="time-range-selector">
                    <button 
                        className={`range-btn ${timeRange === 'day' ? 'active' : ''}`}
                        onClick={() => setTimeRange('day')}
                    >
                        Today
                    </button>
                    <button 
                        className={`range-btn ${timeRange === 'week' ? 'active' : ''}`}
                        onClick={() => setTimeRange('week')}
                    >
                        Week
                    </button>
                    <button 
                        className={`range-btn ${timeRange === 'month' ? 'active' : ''}`}
                        onClick={() => setTimeRange('month')}
                    >
                        Month
                    </button>
                    <button 
                        className={`range-btn ${timeRange === 'year' ? 'active' : ''}`}
                        onClick={() => setTimeRange('year')}
                    >
                        Year
                    </button>
                </div>
            </div>

            {analyticsData && (
                <div className="analytics-content">
                    <div className="metrics-grid">
                        {renderMetricCard(
                            'Total XP',
                            analyticsData.xp?.current || 0,
                            analyticsData.xp?.previous || 0,
                            '⭐',
                            '#007bff'
                        )}
                        {renderMetricCard(
                            'Games Completed',
                            analyticsData.games?.current || 0,
                            analyticsData.games?.previous || 0,
                            '🎮',
                            '#28a745'
                        )}
                        {renderMetricCard(
                            'Badges Earned',
                            analyticsData.badges?.current || 0,
                            analyticsData.badges?.previous || 0,
                            '🏅',
                            '#ffc107'
                        )}
                        {renderMetricCard(
                            'Current Streak',
                            analyticsData.streak?.current || 0,
                            analyticsData.streak?.previous || 0,
                            '🔥',
                            '#dc3545',
                            ' days'
                        )}
                    </div>

                    <div className="analytics-grid">
                        <div className="analytics-section">
                            {renderChart()}
                        </div>
                        <div className="analytics-section">
                            {renderAchievementProgress()}
                        </div>
                    </div>

                    <div className="analytics-section full-width">
                        {renderActivityHeatmap()}
                    </div>

                    <div className="insights-section">
                        <h3>📈 Insights & Recommendations</h3>
                        <div className="insights-grid">
                            <div className="insight-card">
                                <div className="insight-icon">🎯</div>
                                <div className="insight-content">
                                    <h4>Most Active Time</h4>
                                    <p>You're most productive at {analyticsData.insights?.mostActiveTime || '2:00 PM'}. Try scheduling learning sessions around this time!</p>
                                </div>
                            </div>
                            <div className="insight-card">
                                <div className="insight-icon">📚</div>
                                <div className="insight-content">
                                    <h4>Suggested Focus</h4>
                                    <p>You're close to unlocking {analyticsData.insights?.nextAchievement || 'a new achievement'}. Keep it up!</p>
                                </div>
                            </div>
                            <div className="insight-card">
                                <div className="insight-icon">🏆</div>
                                <div className="insight-content">
                                    <h4>Improvement Area</h4>
                                    <p>Try playing more {analyticsData.insights?.suggestedGameType || 'quiz games'} to boost your learning variety!</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AnalyticsDashboard;
