import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import '../styles/GamificationTester.css';

const GamificationTester = () => {
    const { token } = useAuth();
    const [results, setResults] = useState(null);
    const [loading, setLoading] = useState(false);

    const testGameCompletion = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5000/api/gamification/events/game-completed', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    gameId: 'test-game-1',
                    score: 95,
                    accuracy: 100,
                    timeSpent: 120,
                    completed: true,
                    xp: 50
                })
            });

            if (response.ok) {
                const data = await response.json();
                setResults(data);
            }
        } catch (error) {
            console.error('Error testing game completion:', error);
        } finally {
            setLoading(false);
        }
    };

    const awardXP = async (amount) => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5000/api/gamification/events/award-xp', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    amount,
                    source: 'test',
                    description: `Test XP award: +${amount} XP`
                })
            });

            if (response.ok) {
                const data = await response.json();
                setResults(data);
            }
        } catch (error) {
            console.error('Error awarding XP:', error);
        } finally {
            setLoading(false);
        }
    };

    const testAchievement = async () => {
        setLoading(true);
        try {
            // Simulate multiple game completions to trigger achievements
            for (let i = 0; i < 3; i++) {
                await fetch('http://localhost:5000/api/gamification/events/game-completed', {
                    method: 'POST',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        gameId: `test-game-${i + 1}`,
                        score: 85 + (i * 5),
                        accuracy: 90 + (i * 2),
                        timeSpent: 100 + (i * 10),
                        completed: true,
                        xp: 25 + (i * 5)
                    })
                });
            }

            // Get updated progress
            const response = await fetch('http://localhost:5000/api/gamification/progress', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const data = await response.json();
                setResults(data);
            }
        } catch (error) {
            console.error('Error testing achievements:', error);
        } finally {
            setLoading(false);
        }
    };

    const initializeUser = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:5000/api/gamification/initialize', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const data = await response.json();
                setResults(data);
            }
        } catch (error) {
            console.error('Error initializing user:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="gamification-tester">
            <div className="tester-header">
                <h2>üß™ Gamification System Tester</h2>
                <p>Test various gamification features and see real-time results</p>
            </div>

            <div className="test-actions">
                <div className="action-group">
                    <h3>Initial Setup</h3>
                    <button 
                        onClick={initializeUser}
                        disabled={loading}
                        className="test-btn primary"
                    >
                        Initialize User
                    </button>
                </div>

                <div className="action-group">
                    <h3>XP System</h3>
                    <div className="button-group">
                        <button 
                            onClick={() => awardXP(25)}
                            disabled={loading}
                            className="test-btn success"
                        >
                            Award 25 XP
                        </button>
                        <button 
                            onClick={() => awardXP(100)}
                            disabled={loading}
                            className="test-btn success"
                        >
                            Award 100 XP
                        </button>
                        <button 
                            onClick={() => awardXP(500)}
                            disabled={loading}
                            className="test-btn success"
                        >
                            Award 500 XP
                        </button>
                    </div>
                </div>

                <div className="action-group">
                    <h3>Game Events</h3>
                    <div className="button-group">
                        <button 
                            onClick={testGameCompletion}
                            disabled={loading}
                            className="test-btn info"
                        >
                            Complete Perfect Game
                        </button>
                        <button 
                            onClick={testAchievement}
                            disabled={loading}
                            className="test-btn warning"
                        >
                            Complete Multiple Games
                        </button>
                    </div>
                </div>
            </div>

            {loading && (
                <div className="loading-section">
                    <div className="loading-spinner"></div>
                    <p>Processing gamification events...</p>
                </div>
            )}

            {results && (
                <div className="results-section">
                    <h3>Results</h3>
                    <div className="results-grid">
                        {results.data?.xp && (
                            <div className="result-card xp">
                                <h4>XP Gained</h4>
                                <div className="result-value">+{results.data.xp.finalAmount}</div>
                                <div className="result-details">
                                    <p>Original: {results.data.xp.originalAmount}</p>
                                    <p>Multiplier: {results.data.xp.multiplier}x</p>
                                    {results.data.xp.levelUp && (
                                        <p className="level-up">üéâ Level Up!</p>
                                    )}
                                </div>
                            </div>
                        )}

                        {results.data?.badges && results.data.badges.length > 0 && (
                            <div className="result-card badges">
                                <h4>Badges Earned</h4>
                                <div className="badges-list">
                                    {results.data.badges.map((badgeResult, index) => (
                                        <div key={index} className="badge-item">
                                            <span className="badge-icon">{badgeResult.badge?.icon || 'üèÖ'}</span>
                                            <span className="badge-name">{badgeResult.badge?.name}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {results.data?.achievements && results.data.achievements.length > 0 && (
                            <div className="result-card achievements">
                                <h4>Achievements Unlocked</h4>
                                <div className="achievements-list">
                                    {results.data.achievements.map((achResult, index) => (
                                        <div key={index} className="achievement-item">
                                            <span className="achievement-icon">{achResult.achievement?.icon || 'üèÜ'}</span>
                                            <div className="achievement-info">
                                                <span className="achievement-name">{achResult.achievement?.name}</span>
                                                <span className="achievement-desc">{achResult.achievement?.description}</span>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {results.data?.notifications && results.data.notifications.length > 0 && (
                            <div className="result-card notifications">
                                <h4>Notifications</h4>
                                <div className="notifications-list">
                                    {results.data.notifications.map((notif, index) => (
                                        <div key={index} className="notification-item">
                                            <span className="notification-icon">{notif.icon}</span>
                                            <div className="notification-content">
                                                <span className="notification-title">{notif.title}</span>
                                                <span className="notification-message">{notif.message}</span>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="raw-results">
                        <details>
                            <summary>Raw JSON Response</summary>
                            <pre>{JSON.stringify(results, null, 2)}</pre>
                        </details>
                    </div>
                </div>
            )}
        </div>
    );
};

export default GamificationTester;
