import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaArrowLeft, FaRedo, FaStar, FaTrophy, FaCog, FaPlay, FaBolt, FaFire, FaCrown } from 'react-icons/fa';
import axiosInstance from '../../utils/axiosInstance';
import { useAuth } from '../../context/AuthContext';
import './FlashCards.css';

const FlashCards = () => {
    const navigate = useNavigate();
    const { user, token } = useAuth();
    // √âtat du jeu
    const [gameState, setGameState] = useState('configuration'); // 'configuration', 'chargement', 'en_jeu', 'termine'
    const [errorMessage, setErrorMessage] = useState(''); // Pour afficher les erreurs
    const [currentCardIndex, setCurrentCardIndex] = useState(0);
    const [isFlipped, setIsFlipped] = useState(false);
    const [score, setScore] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [wrongAnswers, setWrongAnswers] = useState(0);
    const [cards, setCards] = useState([]);    const [sessionId, setSessionId] = useState(null);
    // const [gameId, setGameId] = useState(null); // Kept for future use
    const [startTime, setStartTime] = useState(null);
    const [cardStartTime, setCardStartTime] = useState(null);
    const [streak, setStreak] = useState(0);
    const [bestStreak, setBestStreak] = useState(0);
    const [showCelebration, setShowCelebration] = useState(false);

    // Param√®tres du jeu
    const [gameSettings, setGameSettings] = useState({
        subject: 'mathematics',
        difficulty: 'moyen',
        cycle: 'cycle_4',
        theme: 'default',
        quantity: 8
    });

    // Mati√®res disponibles et leurs libell√©s (cl√©s en anglais pour la logique interne)
    const subjects = {
        mathematics: { label: 'Math√©matiques', icon: 'üî¢' },
        french: { label: 'Fran√ßais', icon: 'üìö' },
        history: { label: 'Histoire', icon: 'üèõÔ∏è' },
        sciences: { label: 'Sciences', icon: 'üî¨' },
        geography: { label: 'G√©ographie', icon: 'üåç' },
        english: { label: 'Anglais', icon: 'üá¨üáß' }
    };

    const difficulties = {
        facile: { label: 'Facile', color: '#4CAF50' },
        moyen: { label: 'Moyen', color: '#FF9800' },
        difficile: { label: 'Difficile', color: '#F44336' }
    };
    // G√®re le d√©but du minuteur pour chaque carte
    useEffect(() => {
        if (gameState === 'en_jeu' && currentCardIndex < cards.length) {
            setCardStartTime(Date.now());
        }
    }, [currentCardIndex, gameState, cards.length]);

    // D√©marre une nouvelle partie
    const startGame = async () => {
        try {
            console.log('D√©marrage du jeu de Flash Cards...', { user: user?.username, hasToken: !!token });
            setGameState('chargement');
            setErrorMessage(''); // R√©initialiser les erreurs
            setStartTime(Date.now());

            const response = await axiosInstance.post('/games/flash-cards/start', gameSettings);

            console.log('R√©ponse du d√©marrage du jeu:', response.data);
            if (response.data.success) {
                setCards(response.data.cards);
                setSessionId(response.data.sessionId);
                // setGameId(response.data.gameId); // Conserv√© pour une utilisation future
                setGameState('en_jeu');
                setCardStartTime(Date.now());
            } else {
                throw new Error('√âchec du d√©marrage de la partie');
            }
        } catch (error) {
            console.error('Erreur lors du d√©marrage du jeu:', error);
            const errorMsg = error.response?.data?.message || 'Erreur lors du d√©marrage du jeu. V√©rifiez que vous √™tes bien connect√© et r√©essayez.';
            setErrorMessage(errorMsg);
            setGameState('configuration');
        }
    };

    // Soumet la r√©ponse de l'utilisateur pour la carte actuelle
    const submitAnswer = async (isCorrect) => {
        try {
            const responseTime = Date.now() - cardStartTime;
            const currentCard = cards[currentCardIndex];
            // Soumission de la r√©ponse au backend
            await axiosInstance.post('/games/flash-cards/answer', {
                sessionId: sessionId,
                cardId: currentCard.id,
                questionId: currentCard.id,
                isCorrect: isCorrect,
                responseTime: responseTime,
                difficulty: currentCard.difficulty
            });

            // Update local state with enhanced feedback
            if (isCorrect) {
                setCorrectAnswers(prev => prev + 1);
                setScore(prev => prev + getDifficultyPoints(currentCard.difficulty, responseTime));
                setStreak(prev => {
                    const newStreak = prev + 1;
                    if (newStreak > bestStreak) {
                        setBestStreak(newStreak);
                        if (newStreak >= 5) {
                            setShowCelebration(true);
                            setTimeout(() => setShowCelebration(false), 2000);
                        }
                    }
                    return newStreak;
                });
            } else {
                setWrongAnswers(prev => prev + 1);
                setStreak(0);
            }

            // Move to next card after a short delay
            setTimeout(() => {
                if (currentCardIndex < cards.length - 1) {
                    setCurrentCardIndex(prev => prev + 1);
                    setIsFlipped(false);
                } else {
                    completeGame();
                }
            }, 1500);

        } catch (error) {
            console.error('Erreur lors de la soumission de la r√©ponse:', error);
        }
    };

    // Finalise la partie et envoie les r√©sultats
    const completeGame = async () => {
        try {
            const totalTime = Date.now() - startTime;
            const averageResponseTime = totalTime / cards.length;            const response = await axiosInstance.post('/games/flash-cards/complete', {
                sessionId: sessionId,
                totalScore: score,
                correctAnswers: correctAnswers,
                wrongAnswers: wrongAnswers,
                totalTime: totalTime,
                averageResponseTime: averageResponseTime,
                difficulty: gameSettings.difficulty
            });

            if (response.data.success) {
                setGameState('termine');
            }
        } catch (error) {
            console.error('Erreur lors de la finalisation du jeu:', error);
