    // G√®re la r√©ponse de l'utilisateur (correcte ou incorrecte)
    const handleAnswer = (isCorrect) => {
        submitAnswer(isCorrect);
    };

    // G√®re le retournement de la carte
    const handleCardFlip = () => {
        setIsFlipped(!isFlipped);
