        const basePoints = { facile: 10, moyen: 20, difficile: 30 };
        const timeBonus = Math.max(0, 10 - Math.floor(responseTime / 1000));
        return (basePoints[difficulty] || 10) + timeBonus;
    };

    const getSuccessRate = () => {
        const total = correctAnswers + wrongAnswers;
        return total > 0 ? Math.round((correctAnswers / total) * 100) : 0;
    };

    // Red√©marre le jeu
    const restartGame = () => {
        setCurrentCardIndex(0);
        setIsFlipped(false);
        setScore(0);
        setCorrectAnswers(0);
        setWrongAnswers(0);
        setStreak(0);
        setShowCelebration(false);
        setGameState('configuration');
        setCards([]);
        setSessionId(null);
    };

    // Retourne un message de performance bas√© sur le taux de r√©ussite
    const getPerformanceMessage = () => {
        const rate = getSuccessRate();
        if (rate >= 95) return "Parfait ! Tu es un vrai champion ! üèÜ";
        if (rate >= 85) return "Excellent travail ! Continue comme √ßa ! üåü";
        if (rate >= 75) return "Tr√®s bien ! Tu progresses ! üëè";
        if (rate >= 60) return "Pas mal ! Il y a de l'am√©lioration ! üí™";
        return "Continue √† t'entra√Æner, tu vas y arriver ! üìö";
    };

    // Retourne le niveau de performance bas√© sur le taux de r√©ussite
    const getPerformanceLevel = () => {
        const rate = getSuccessRate();
        if (rate >= 95) return 'üëë Ma√Ætre';
        if (rate >= 85) return 'üåü Expert';
        if (rate >= 75) return '‚≠ê Avanc√©';
        if (rate >= 60) return 'üí™ Apprenti';
        return 'üìö D√©butant';
    };

    // √âcran de configuration du jeu
    if (gameState === 'configuration') {
        return (
            <div className="flash-cards-container">
                <div className="flash-cards-header">
                    <button className="back-button" onClick={() => navigate(-1)}>
                        <FaArrowLeft /> Retour
                    </button>
                    <h1><FaBolt /> Flash Cards Magiques</h1>
                    <div className="header-right">
                        <div className="score-display">
                            <FaStar />
                            {score} points
                        </div>
                        {streak > 0 && (
                            <div className={`streak-display ${streak >= 3 ? 'fire' : ''}`}>
                                {streak >= 3 ? <FaFire /> : <FaBolt />}
                                {streak} en s√©rie!
                            </div>
                        )}
                        <FaCog className="settings-icon" />
                    </div>
                </div>

                <div className="game-setup">
                    <div className="setup-card">                        <h2>üéØ Pr√™t pour le D√©fi Magique?</h2>
                        <p>Configure ta session de Flash Cards et teste tes connaissances! ‚ú®</p>

                        {errorMessage && (
                            <div className="error-message-box">
                                <p>{errorMessage}</p>
                            </div>
                        )}

                        <div className="settings-grid">
                            <div className="setting-group">
                                <label>üìö Mati√®re</label>
                                <select 
                                    value={gameSettings.subject}
                                    onChange={(e) => setGameSettings(prev => ({...prev, subject: e.target.value}))}
                                >
                                    {Object.entries(subjects).map(([key, subj]) => (
                                        <option key={key} value={key}>
                                            {subj.icon} {subj.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <div className="setting-group">
                                <label>üéØ Difficult√©</label>
                                <select 
                                    value={gameSettings.difficulty}
                                    onChange={(e) => setGameSettings(prev => ({...prev, difficulty: e.target.value}))}
                                >
                                    {Object.entries(difficulties).map(([key, diff]) => (
                                        <option key={key} value={key}>
                                            {diff.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <div className="setting-group">
                                <label>üé≤ Nombre de cartes</label>
                                <select 
                                    value={gameSettings.quantity}
                                    onChange={(e) => setGameSettings(prev => ({...prev, quantity: parseInt(e.target.value)}))}
                                >
                                    <option value={5}>5 cartes (Rapide)</option>
                                    <option value={8}>8 cartes (Normal)</option>
                                    <option value={10}>10 cartes (Complet)</option>
                                    <option value={15}>15 cartes (Expert)</option>
                                </select>
                            </div>
                        </div>

                        <div className="difficulty-preview">
                            <div 
                                className="difficulty-indicator"
                                style={{ backgroundColor: difficulties[gameSettings.difficulty].color }}
                            >
                                {difficulties[gameSettings.difficulty].label}
                            </div>
                            <p>
                                Sujet: <strong>{subjects[gameSettings.subject].label}</strong> ‚Ä¢ 
                                Cartes: <strong>{gameSettings.quantity}</strong>
                            </p>
                        </div>                        <button 
                            className="start-game-button" 
                            onClick={startGame}
                            aria-label="Lancer la session de Flash Cards"
                        >
                            <FaPlay />
                            Lancer la Session Magique!
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    // √âcran de chargement
    if (gameState === 'chargement') {
        return (
            <div className="flash-cards-container loading-screen">
                <div className="loading-spinner"></div>
                <h2>Pr√©paration des cartes...</h2>
                <p>Un peu de patience, l'aventure commence bient√¥t !</p>
            </div>
        );
    }

    // √âcran de jeu principal
    if (gameState === 'en_jeu' && cards.length > 0) {
        const currentCard = cards[currentCardIndex];
        const progressPercentage = ((currentCardIndex + 1) / cards.length) * 100;

        return (
            <div className="flash-cards-container">
                {/* Celebration overlay */}
                {showCelebration && (
                    <div className="celebration-overlay">                    <div className="celebration-content">
                            <FaCrown className="celebration-icon" />
                            <h2>Incroyable !</h2>
                            <p>S√©rie de {bestStreak} bonnes r√©ponses ! üî•</p>
                            <div className="celebration-sparkles">
                                <span>‚ú®</span>
                                <span>üåü</span>
                                <span>‚≠ê</span>
                                <span>‚ú®</span>
                                <span>üåü</span>
                            </div>
                        </div>
                    </div>
                )}                <div className="flash-cards-header">
                    <button className="back-button" onClick={() => navigate(-1)}>
                        <FaArrowLeft /> Retour
                    </button>
                    <h1><FaBolt /> Flash Cards Magiques</h1>
                    <div className="header-right">
                        <div className="score-display">
                            <FaStar />
