import React, { useState } from 'react';
import './BadgeDisplay.css';

const BadgeDisplay = ({ 
    badge, 
    userBadge, 
    size = 'medium', 
    showTooltip = true,
    isNew = false,
    onClick = null,
    earned = false
}) => {
    const [showDetails, setShowDetails] = useState(false);

    const getRarityColor = (rarity) => {
        switch (rarity) {
            case 'common': return '#9e9e9e';
            case 'rare': return '#2196f3';
            case 'epic': return '#9c27b0';
            case 'legendary': return '#ff9800';
            default: return '#9e9e9e';
        }
    };

    const getRarityName = (rarity) => {
        return rarity.charAt(0).toUpperCase() + rarity.slice(1);
    };

    const getBadgeSize = () => {
        switch (size) {
            case 'small': return '40px';
            case 'large': return '80px';
            case 'xlarge': return '120px';
            default: return '60px'; // medium
        }
    };

    const formatDate = (date) => {
        return new Date(date).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric'
        });
    };

    const handleClick = () => {
        if (onClick) {
            onClick(badge, userBadge);
        }
    };

    const handleMouseEnter = () => {
        if (showTooltip) {
            setShowDetails(true);
        }
    };

    const handleMouseLeave = () => {
        if (showTooltip) {
            setShowDetails(false);
        }
    };

    return (
        <div 
            className={`badge-display ${size} ${earned ? 'earned' : 'locked'} ${isNew ? 'new' : ''}`}
            onClick={handleClick}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            style={{ cursor: onClick ? 'pointer' : 'default' }}
        >
            <div 
                className="badge-container"
                style={{
                    width: getBadgeSize(),
                    height: getBadgeSize(),
                    borderColor: getRarityColor(badge.rarity)
                }}
            >
                <div className="badge-icon">
                    {badge.icon}
                </div>
                
                {!earned && (
                    <div className="badge-lock-overlay">
                        <span className="lock-icon">ðŸ”’</span>
                    </div>
                )}
                
                {isNew && earned && (
                    <div className="badge-new-indicator">
                        <span className="new-text">NEW!</span>
                    </div>
                )}
                
                <div 
                    className="badge-rarity-border"
                    style={{ borderColor: getRarityColor(badge.rarity) }}
                ></div>
                
                {earned && (
                    <div className="badge-shine"></div>
                )}
            </div>
            
            {size !== 'small' && (
                <div className="badge-info">
                    <h4 className="badge-name">{badge.name}</h4>
                    <p className="badge-rarity" style={{ color: getRarityColor(badge.rarity) }}>
                        {getRarityName(badge.rarity)}
                    </p>
                </div>
            )}
            
            {showDetails && showTooltip && (
                <div className="badge-tooltip">
                    <div className="tooltip-header">
                        <h3 className="tooltip-title">{badge.name}</h3>
                        <span 
                            className="tooltip-rarity"
                            style={{ color: getRarityColor(badge.rarity) }}
                        >
                            {getRarityName(badge.rarity)}
                        </span>
                    </div>
                    
                    <p className="tooltip-description">{badge.description}</p>
                    
                    <div className="tooltip-stats">
                        <div className="stat-item">
                            <span className="stat-label">Category:</span>
                            <span className="stat-value">{badge.category}</span>
                        </div>
                        
                        {badge.xpReward > 0 && (
                            <div className="stat-item">
                                <span className="stat-label">XP Reward:</span>
                                <span className="stat-value">+{badge.xpReward} XP</span>
                            </div>
                        )}
                        
                        {earned && userBadge && (
                            <div className="stat-item">
                                <span className="stat-label">Earned:</span>
                                <span className="stat-value">{formatDate(userBadge.earnedAt)}</span>
                            </div>
                        )}
                    </div>
                    
                    {!earned && (
                        <div className="tooltip-progress">
                            <p className="progress-text">
                                {badge.requirements && typeof badge.requirements === 'object' 
                                    ? 'Complete the requirements to unlock this badge'
                                    : 'Keep playing to unlock this badge!'
                                }
                            </p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

const BadgeGrid = ({ badges = [], userBadges = [], onBadgeClick = null, showLocked = true }) => {
    const userBadgeMap = new Map(userBadges.map(ub => [ub.badgeId, ub]));
    
    const sortedBadges = [...badges].sort((a, b) => {
        const aEarned = userBadgeMap.has(a.id);
        const bEarned = userBadgeMap.has(b.id);
        
        // Earned badges first
        if (aEarned && !bEarned) return -1;
        if (!aEarned && bEarned) return 1;
        
        // Then by rarity
        const rarityOrder = { legendary: 0, epic: 1, rare: 2, common: 3 };
        const rarityDiff = (rarityOrder[a.rarity] || 3) - (rarityOrder[b.rarity] || 3);
        if (rarityDiff !== 0) return rarityDiff;
        
        // Finally by sort order and name
        return (a.sortOrder || 999) - (b.sortOrder || 999) || a.name.localeCompare(b.name);
    });

    return (
        <div className="badge-grid">
            {sortedBadges.map(badge => {
                const userBadge = userBadgeMap.get(badge.id);
                const earned = !!userBadge;
                
                if (!earned && !showLocked) return null;
                
                return (
                    <BadgeDisplay
                        key={badge.id}
                        badge={badge}
                        userBadge={userBadge}
                        earned={earned}
                        isNew={userBadge?.isNew || false}
                        onClick={onBadgeClick}
                        size="medium"
                        showTooltip={true}
                    />
                );
            })}
        </div>
    );
};

export default BadgeDisplay;
export { BadgeGrid };
