import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';
import {
    FaUser,
    FaEdit,
    FaCamera,
    FaTrophy,
    FaStar,
    FaGamepad,
    FaUserPlus,
    FaUsers,
    FaHeart,
    FaComment,
    FaGem,
    FaChevronLeft,
    FaChevronRight,
    FaExclamationCircle
} from 'react-icons/fa';
import './Profile.css';

function Profile() {
    const [profileData, setProfileData] = useState(null);
    const [posts, setPosts] = useState([]);
    const [stats, setStats] = useState({
        followers: 0,
        following: 0,
        posts: 0
    });
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('overview'); // overview, posts, stats
    const [notification, setNotification] = useState({ show: false, message: '', type: 'info' });
    const [postsPage, setPostsPage] = useState(1);
    const [totalPostsPages, setTotalPostsPages] = useState(1);
    const [achievements, setAchievements] = useState([]);
    const [loadingStates, setLoadingStates] = useState({
        profile: true,
        posts: true,
        stats: true,
        achievements: true
    });
    const POSTS_PER_PAGE = 6;

    const { user, token } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            navigate('/connexion');
            return;
        }
        
        // Fetch all data in parallel
        const fetchAllData = async () => {
            setLoading(true);
            try {
                await Promise.all([
                    fetchProfileData(),
                    fetchUserPosts(),
                    fetchUserStats(),
                    fetchAchievements()
                ]);
            } catch (error) {
                console.error('Error fetching profile data:', error);
            } finally {
                setLoading(false);
            }
        };
        
        fetchAllData();
    }, [user, token, navigate]);

    const showNotification = useCallback((message, type = 'info') => {
        setNotification({ show: true, message, type });
        setTimeout(() => {
            setNotification({ show: false, message: '', type: 'info' });
        }, 3000);
    }, []);

    const fetchProfileData = async () => {
        setLoadingStates(prev => ({ ...prev, profile: true }));
        try {
            const response = await axios.get(
                'http://localhost:5000/api/auth/me',
                {
                    headers: { Authorization: `Bearer ${token}` }
                }
            );
            setProfileData(response.data);
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration du profil:', error);
            showNotification('Erreur lors du chargement du profil', 'error');
        } finally {
            setLoadingStates(prev => ({ ...prev, profile: false }));
        }
    };

    const fetchUserPosts = async (page = 1) => {
        setLoadingStates(prev => ({ ...prev, posts: true }));
        try {
            const response = await axios.get(
                `http://localhost:5000/api/posts/user/${user.id}`,
                {
                    headers: { Authorization: `Bearer ${token}` },
                    params: {
                        page,
                        limit: POSTS_PER_PAGE
                    }
                }
            );
            setPosts(response.data.posts || response.data || []);
            setTotalPostsPages(response.data.totalPages || Math.ceil((response.data.length || 0) / POSTS_PER_PAGE));
            setPostsPage(page);
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration des posts:', error);
            setPosts([]);
            showNotification('Erreur lors du chargement des publications', 'error');
        } finally {
            setLoadingStates(prev => ({ ...prev, posts: false }));
        }
    };

    const fetchUserStats = async () => {
        setLoadingStates(prev => ({ ...prev, stats: true }));
        try {
            const [followersRes, followingRes, postsRes] = await Promise.all([
                axios.get('http://localhost:5000/api/auth/followers', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                axios.get('http://localhost:5000/api/auth/following', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                axios.get(`http://localhost:5000/api/posts/user/${user.id}/count`, {
                    headers: { Authorization: `Bearer ${token}` }
                }).catch(() => ({ data: { count: 0 } }))
            ]);

            setStats({
                followers: followersRes.data.length || 0,
                following: followingRes.data.length || 0,
                posts: postsRes.data.count || posts.length || 0
            });
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration des statistiques:', error);
            setStats({ followers: 0, following: 0, posts: 0 });
            showNotification('Erreur lors du chargement des statistiques', 'error');
        } finally {
            setLoadingStates(prev => ({ ...prev, stats: false }));
        }
    };

    const fetchAchievements = async () => {
        setLoadingStates(prev => ({ ...prev, achievements: true }));
        try {
            const response = await axios.get(
                `http://localhost:5000/api/achievements/user/${user.id}`,
                {
                    headers: { Authorization: `Bearer ${token}` }
                }
            );
            setAchievements(response.data || []);
        } catch (error) {
            console.error('Erreur lors de la rÃ©cupÃ©ration des accomplissements:', error);
            setAchievements([]);
        } finally {
            setLoadingStates(prev => ({ ...prev, achievements: false }));
        }
    };

    const handleProfilePictureUpload = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        // Validate file size (max 5MB)
        if (file.size > 5 * 1024 * 1024) {
            showNotification('La taille du fichier ne doit pas dÃ©passer 5MB', 'error');
            return;
        }

        // Validate file type
        const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
        if (!allowedTypes.includes(file.type)) {
            showNotification('Format de fichier non supportÃ©. Utilisez JPG, PNG, GIF ou WebP', 'error');
            return;
        }

        const formData = new FormData();
        formData.append('profilePicture', file);

        try {
            showNotification('TÃ©lÃ©chargement en cours...', 'info');
            
            await axios.put(
                'http://localhost:5000/api/auth/me/profile-picture',
                formData,
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'multipart/form-data'
                    }
                }
            );

            // Mettre Ã  jour les donnÃ©es du profil
            await fetchProfileData();
            showNotification('Photo de profil mise Ã  jour avec succÃ¨s!', 'success');
        } catch (error) {
            console.error('Erreur lors de la mise Ã  jour de la photo:', error);
            showNotification('Erreur lors de la mise Ã  jour de la photo', 'error');
        }
    };

    // Skeleton loader component
    const SkeletonLoader = () => (
        <div className="profile-page">
            <div className="skeleton-header">
                <div className="skeleton-avatar-section">
                    <div className="skeleton skeleton-avatar"></div>
                    <div className="skeleton-info">
                        <div className="skeleton skeleton-text large"></div>
                        <div className="skeleton skeleton-text medium"></div>
                        <div className="skeleton skeleton-text small"></div>
                        <div className="skeleton-stats">
                            <div className="skeleton skeleton-stat"></div>
                            <div className="skeleton skeleton-stat"></div>
                            <div className="skeleton skeleton-stat"></div>
                        </div>
                    </div>
                </div>
            </div>
            
            <div className="skeleton-content">
                <div className="skeleton-tabs">
                    <div className="skeleton skeleton-tab"></div>
                    <div className="skeleton skeleton-tab"></div>
                    <div className="skeleton skeleton-tab"></div>
                </div>
                
                <div className="skeleton-cards-grid">
                    <div className="skeleton skeleton-card large"></div>
                    <div className="skeleton skeleton-card"></div>
                    <div className="skeleton skeleton-card"></div>
                    <div className="skeleton skeleton-card"></div>
                    <div className="skeleton skeleton-card"></div>
                </div>
                
                <div className="skeleton skeleton-achievements"></div>
                
                <div className="skeleton-actions">
                    <div className="skeleton skeleton-action"></div>
                    <div className="skeleton skeleton-action"></div>
                    <div className="skeleton skeleton-action"></div>
                </div>
            </div>
        </div>
    );

    if (loading || !profileData) {
        return <SkeletonLoader />;
    }

    const renderOverviewTab = () => (
        <div className="overview-tab">
            <section className="profile-stats" aria-label="Statistiques de jeu">
                <article className="stat-card" role="article" aria-labelledby="level-heading">
                    <div className="stat-icon" aria-hidden="true">
                        <FaTrophy />
                    </div>
                    <div className="stat-info">
                        <h3 id="level-heading">Niveau</h3>
                        <p className="stat-value" aria-label={`Niveau actuel: ${profileData.level || 1}`}>{profileData.level || 1}</p>
                        {profileData.xpToNextLevel !== undefined && (
                            <div className="level-progress" aria-label="Progression vers le niveau suivant">
                                <div 
                                    className="progress-bar" 
                                    role="progressbar"
                                    aria-valuenow={profileData.currentLevelXP || 0}
                                    aria-valuemin="0"
                                    aria-valuemax={profileData.nextLevelXP || 1}
                                    aria-label="Progression du niveau"
                                >
                                    <div 
                                        className="progress-fill" 
                                        style={{ 
                                            width: `${((profileData.currentLevelXP || 0) / (profileData.nextLevelXP || 1)) * 100}%` 
                                        }}
                                    ></div>
                                </div>
                                <small className="progress-text">
                                    {profileData.xpToNextLevel || 0} XP jusqu'au prochain niveau
                                </small>
                            </div>
                        )}
                    </div>
                </article>

                <article className="stat-card" role="article" aria-labelledby="xp-heading">
                    <div className="stat-icon" aria-hidden="true">
                        <FaStar />
                    </div>
                    <div className="stat-info">
                        <h3 id="xp-heading">XP Total</h3>
                        <p className="stat-value" aria-label={`${profileData.totalXP || 0} points d'expÃ©rience au total`}>{profileData.totalXP || 0}</p>
                    </div>
                </article>

                <article className="stat-card" role="article" aria-labelledby="fragments-heading">
                    <div className="stat-icon" aria-hidden="true">
                        <FaGem />
                    </div>
                    <div className="stat-info">
                        <h3 id="fragments-heading">Fragments</h3>
                        <p className="stat-value" aria-label={`${profileData.fragments || 0} fragments collectÃ©s`}>{profileData.fragments || 0}</p>
                    </div>
                </article>

                <article className="stat-card" role="article" aria-labelledby="quests-heading">
                    <div className="stat-icon" aria-hidden="true">
                        <FaGamepad />
                    </div>
                    <div className="stat-info">
                        <h3 id="quests-heading">QuÃªtes</h3>
                        <p className="stat-value" aria-label={`${profileData.quests_completed || 0} quÃªtes complÃ©tÃ©es`}>{profileData.quests_completed || 0}</p>
                    </div>
                </article>

                <article className="stat-card" role="article" aria-labelledby="followers-heading">
                    <div className="stat-icon" aria-hidden="true">
                        <FaUsers />
                    </div>
                    <div className="stat-info">
                        <h3 id="followers-heading">AbonnÃ©s</h3>
                        <p className="stat-value" aria-label={`${stats.followers} abonnÃ©s`}>{stats.followers}</p>
                    </div>
                </article>
            </section>

            <section className="recent-achievements" aria-labelledby="achievements-heading">
                <h3 id="achievements-heading">Derniers accomplissements</h3>
                {loadingStates.achievements ? (
                    <div className="loading-section">
                        <div className="spinner-small"></div>
                        <span>Chargement des accomplissements...</span>
                    </div>
                ) : achievements.length > 0 ? (
                    <div className="achievements-list">
                        {achievements.slice(0, 3).map((achievement) => (
                            <div key={achievement.id} className="achievement-item">
                                <div className="achievement-icon">{achievement.icon || 'ðŸ†'}</div>
                                <div className="achievement-details">
                                    <h4>{achievement.name}</h4>
                                    <p>{achievement.description}</p>
                                    <small>{new Date(achievement.earned_at).toLocaleDateString('fr-FR')}</small>
                                </div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <div className="no-achievements" role="status" aria-label="Aucun accomplissement">
                        <p>Aucun accomplissement pour le moment. Continue tes quÃªtes!</p>
                    </div>
                )}
            </section>

            <nav className="profile-actions" aria-label="Actions du profil">
                <Link to="/modifier-profil" className="action-btn primary" aria-label="Modifier les informations du profil">
                    <FaEdit aria-hidden="true" /> Modifier le profil
                </Link>

                {user.accountType === 'parent' && (
                    <Link to="/parent-dashboard" className="action-btn secondary" aria-label="AccÃ©der au tableau de bord parental">
                        <FaUserPlus aria-hidden="true" /> Gestion Enfants
                    </Link>
                )}

                <Link to="/reseau" className="action-btn secondary" aria-label="AccÃ©der au rÃ©seau social">
                    <FaUsers aria-hidden="true" /> RÃ©seau Social
                </Link>
            </nav>
        </div>
    );

    const renderPostsTab = () => {
        const startIndex = (postsPage - 1) * POSTS_PER_PAGE;
        const paginatedPosts = posts.slice(startIndex, startIndex + POSTS_PER_PAGE);

        return (
            <div className="posts-tab">
                <div className="posts-header">
                    <h3>Mes publications ({stats.posts})</h3>
                    <Link to="/create-post" className="create-post-btn">
                        <FaEdit /> Nouvelle publication
                    </Link>
                </div>

                {loadingStates.posts ? (
                    <div className="loading-section">
                        <div className="spinner-small"></div>
                        <span>Chargement des publications...</span>
                    </div>
                ) : posts.length === 0 ? (
                <div className="no-posts">
                    <FaComment className="no-posts-icon" />
                    <h4>Aucune publication</h4>
                    <p>Partagez vos premiers moments avec la communautÃ© !</p>
                    <Link to="/create-post" className="action-btn primary">
                        CrÃ©er ma premiÃ¨re publication
                    </Link>
                </div>
                ) : (
                    <>
                        <div className="posts-grid">
                            {paginatedPosts.map((post) => (
                                <div key={post.id} className="post-preview">
                                    {post.image && (
                                        <div className="post-image">
                                            <img
                                                src={`http://localhost:5000/uploads/${encodeURIComponent(post.image)}`}
                                                alt={`Publication de ${profileData?.name || 'utilisateur'}`}
                                                loading="lazy"
                                                decoding="async"
                                                onError={(e) => {
                                                    e.target.style.display = 'none';
                                                }}
                                                style={{
                                                    aspectRatio: '16/9',
                                                    objectFit: 'cover'
                                                }}
                                            />
                                        </div>
                                    )}
                                    <div className="post-content">
                                        <p>{post.content}</p>
                                        <div className="post-stats">
                                            <span><FaHeart /> {post.likeCount || 0}</span>
                                            <span><FaComment /> {post.commentCount || 0}</span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                        
                        {totalPostsPages > 1 && (
                            <div className="pagination">
                                <button
                                    className="pagination-btn"
                                    onClick={() => fetchUserPosts(postsPage - 1)}
                                    disabled={postsPage === 1}
                                >
                                    <FaChevronLeft />
                                </button>
                                <span className="pagination-info">
                                    Page {postsPage} sur {totalPostsPages}
                                </span>
                                <button
                                    className="pagination-btn"
                                    onClick={() => fetchUserPosts(postsPage + 1)}
                                    disabled={postsPage === totalPostsPages}
                                >
                                    <FaChevronRight />
                                </button>
                            </div>
                        )}
                    </>
                )}
            </div>
        );
    };

    const renderStatsTab = () => (
        <div className="stats-tab">
            <div className="detailed-stats">
                <div className="stats-section">
                    <h3>Statistiques de jeu</h3>
                    <div className="stats-grid">
                        <div className="stat-detail">
                            <span className="stat-label">Temps de jeu total</span>
                            <span className="stat-value">24h 30m</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">QuÃªtes complÃ©tÃ©es</span>
                            <span className="stat-value">{profileData.quests_completed || 0}</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">Fragments collectÃ©s</span>
                            <span className="stat-value">{profileData.fragments || 0}</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">Rang actuel</span>
                            <span className="stat-value">{profileData.rank || 'DÃ©butant'}</span>
                        </div>
                    </div>
                </div>

                <div className="stats-section">
                    <h3>Statistiques sociales</h3>
                    <div className="stats-grid">
                        <div className="stat-detail">
                            <span className="stat-label">AbonnÃ©s</span>
                            <span className="stat-value">{stats.followers}</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">Abonnements</span>
                            <span className="stat-value">{stats.following}</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">Publications</span>
                            <span className="stat-value">{stats.posts}</span>
                        </div>
                        <div className="stat-detail">
                            <span className="stat-label">Membre depuis</span>
                            <span className="stat-value">
                                {new Date(profileData.created_at || Date.now()).toLocaleDateString('fr-FR')}
                            </span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="profile-page">
            {notification.show && (
                <div 
                    className={`notification notification-${notification.type}`}
                    role="alert"
                    aria-live="polite"
                    aria-atomic="true"
                >
                    {notification.type === 'error' && <FaExclamationCircle aria-hidden="true" />}
                    {notification.message}
                </div>
            )}
            <header className="profile-header" role="banner">
                <div className="profile-cover">
                    <div className="profile-avatar-section">
                        <div className="profile-avatar" role="img" aria-label={`Photo de profil de ${profileData.name}`}>
                            {profileData.profilePicture ? (
                                <img
                                    src={`http://localhost:5000/uploads/${encodeURIComponent(profileData.profilePicture)}`}
                                    alt={`Photo de profil de ${profileData.name}`}
                                    onError={(e) => {
                                        e.target.style.display = 'none';
                                        e.target.nextSibling.style.display = 'flex';
                                    }}
                                />
                            ) : (
                                <div className="avatar-placeholder" aria-label={`Initiale de ${profileData.name}`}>
                                    {profileData.name?.charAt(0).toUpperCase() || '?'}
                                </div>
                            )}
                            <label className="avatar-upload" htmlFor="avatar-upload-input" aria-label="TÃ©lÃ©charger une photo de profil">
                                <FaCamera aria-hidden="true" />
                                <input
                                    id="avatar-upload-input"
                                    type="file"
                                    accept="image/jpeg,image/png,image/gif,image/webp"
                                    onChange={handleProfilePictureUpload}
                                    style={{ display: 'none' }}
                                    aria-describedby="upload-help"
                                />
                                <span id="upload-help" className="sr-only">
                                    Formats acceptÃ©s: JPG, PNG, GIF, WebP. Taille maximale: 5MB.
                                </span>
                            </label>
                        </div>

                        <div className="profile-info">
                            <h1 id="profile-name">{profileData.name}</h1>
                            <p className="username" aria-label={`Nom d'utilisateur: ${profileData.username}`}>@{profileData.username}</p>
                            <p className="account-type" aria-label="Type de compte">
                                Compte {profileData.accountType === 'child' ? 'Enfant' :
                                profileData.accountType === 'parent' ? 'Parent' : 'Professeur'}
                            </p>
                            <div className="profile-quick-stats" role="list" aria-label="Statistiques rapides">
                                <span role="listitem" aria-label={`Niveau ${profileData.level || 1}`}>Niveau {profileData.level || 1}</span>
                                <span aria-hidden="true">â€¢</span>
                                <span role="listitem" aria-label={`${profileData.totalXP || 0} points d'expÃ©rience`}>{profileData.totalXP || 0} XP</span>
                                <span aria-hidden="true">â€¢</span>
                                <span role="listitem" aria-label={`${profileData.fragments || 0} fragments collectÃ©s`}>{profileData.fragments || 0} fragments</span>
                                <span aria-hidden="true">â€¢</span>
                                <span role="listitem" aria-label={`${stats.followers} abonnÃ©s`}>{stats.followers} abonnÃ©s</span>
                            </div>
                        </div>
                    </div>
                </div>
            </header>

            <div className="profile-content">
                <div className="profile-tabs" role="tablist" aria-label="Sections du profil">
                    <button
                        className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
                        onClick={() => setActiveTab('overview')}
                        role="tab"
                        aria-selected={activeTab === 'overview'}
                        aria-controls="overview-panel"
                        id="overview-tab"
                        tabIndex={activeTab === 'overview' ? 0 : -1}
                        onKeyDown={(e) => {
                            if (e.key === 'ArrowRight') {
                                setActiveTab('posts');
                                document.getElementById('posts-tab')?.focus();
                            } else if (e.key === 'ArrowLeft') {
                                setActiveTab('stats');
                                document.getElementById('stats-tab')?.focus();
                            }
                        }}
                    >
                        <FaUser aria-hidden="true" /> Vue d'ensemble
                    </button>
                    <button
                        className={`tab-btn ${activeTab === 'posts' ? 'active' : ''}`}
                        onClick={() => setActiveTab('posts')}
                        role="tab"
                        aria-selected={activeTab === 'posts'}
                        aria-controls="posts-panel"
                        id="posts-tab"
                        tabIndex={activeTab === 'posts' ? 0 : -1}
                        onKeyDown={(e) => {
                            if (e.key === 'ArrowRight') {
                                setActiveTab('stats');
                                document.getElementById('stats-tab')?.focus();
                            } else if (e.key === 'ArrowLeft') {
                                setActiveTab('overview');
                                document.getElementById('overview-tab')?.focus();
                            }
                        }}
                    >
                        <FaComment aria-hidden="true" /> Publications
                    </button>
                    <button
                        className={`tab-btn ${activeTab === 'stats' ? 'active' : ''}`}
                        onClick={() => setActiveTab('stats')}
                        role="tab"
                        aria-selected={activeTab === 'stats'}
                        aria-controls="stats-panel"
                        id="stats-tab"
                        tabIndex={activeTab === 'stats' ? 0 : -1}
                        onKeyDown={(e) => {
                            if (e.key === 'ArrowRight') {
                                setActiveTab('overview');
                                document.getElementById('overview-tab')?.focus();
                            } else if (e.key === 'ArrowLeft') {
                                setActiveTab('posts');
                                document.getElementById('posts-tab')?.focus();
                            }
                        }}
                    >
                        <FaTrophy aria-hidden="true" /> Statistiques
                    </button>
                </div>

                <div className="tab-content">
                    {activeTab === 'overview' && (
                        <div role="tabpanel" id="overview-panel" aria-labelledby="overview-tab">
                            {renderOverviewTab()}
                        </div>
                    )}
                    {activeTab === 'posts' && (
                        <div role="tabpanel" id="posts-panel" aria-labelledby="posts-tab">
                            {renderPostsTab()}
                        </div>
                    )}
                    {activeTab === 'stats' && (
                        <div role="tabpanel" id="stats-panel" aria-labelledby="stats-tab">
                            {renderStatsTab()}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Profile;
