import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { API_BASE_URL } from '../config';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const [activeTab, setActiveTab] = useState('dashboard');
    const [stats, setStats] = useState({});
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        // VÃ©rifier si l'utilisateur est admin
        if (!user || user.accountType !== 'admin') {
            navigate('/');
            return;
        }
        
        fetchDashboardData();
    }, [user, navigate]);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            
            // RÃ©cupÃ©rer les statistiques
            const statsResponse = await fetch('/api/admin/stats', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            
            if (statsResponse.ok) {
                const statsData = await statsResponse.json();
                setStats(statsData);
            }

            // RÃ©cupÃ©rer la liste des utilisateurs
            const usersResponse = await fetch('/api/admin/users?limit=10', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            
            if (usersResponse.ok) {
                const usersData = await usersResponse.json();
                setUsers(usersData.users || []);
            }
            
        } catch (error) {
            console.error('Erreur lors du chargement des donnÃ©es:', error);
            setError('Erreur lors du chargement des donnÃ©es');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/');
    };

    if (loading) {
        return (
            <div className="admin-dashboard">
                <div className="admin-loading">
                    <div className="spinner"></div>
                    <p>Chargement du tableau de bord...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="admin-dashboard">
            <div className="admin-header">
                <div className="admin-header-left">
                    <h1>ğŸ›¡ï¸ Administration</h1>
                    <span className="admin-subtitle">Panneau de contrÃ´le</span>
                </div>
                <div className="admin-header-right">
                    <span className="admin-user-info">
                        ğŸ‘¤ {user?.name} 
                        {user?.isSuperAdmin && <span className="super-admin-badge">SUPER ADMIN</span>}
                    </span>
                    <button onClick={handleLogout} className="logout-btn">
                        DÃ©connexion
                    </button>
                </div>
            </div>

            <div className="admin-content">
                <nav className="admin-nav">
                    <button 
                        className={`nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
                        onClick={() => setActiveTab('dashboard')}
                    >
                        ğŸ“Š Tableau de bord
                    </button>
                    <button 
                        className={`nav-item ${activeTab === 'users' ? 'active' : ''}`}
                        onClick={() => setActiveTab('users')}
                    >
                        ğŸ‘¥ Utilisateurs
                    </button>
                    <button 
                        className={`nav-item ${activeTab === 'reports' ? 'active' : ''}`}
                        onClick={() => setActiveTab('reports')}
                    >
                        ğŸš¨ Signalements
                    </button>
                    <button 
                        className={`nav-item ${activeTab === 'logs' ? 'active' : ''}`}
                        onClick={() => setActiveTab('logs')}
                    >
                        ğŸ“‹ Logs
                    </button>
                </nav>

                <main className="admin-main">
                    {error && <div className="error-message">{error}</div>}
                    
                    {activeTab === 'dashboard' && (
                        <DashboardStats stats={stats} />
                    )}
                    
                    {activeTab === 'users' && (
                        <UsersManagement users={users} onRefresh={fetchDashboardData} />
                    )}
                    
                    {activeTab === 'reports' && (
                        <div className="coming-soon">
                            <h2>ğŸš¨ Gestion des signalements</h2>
                            <p>Cette fonctionnalitÃ© sera disponible dans la Phase 2</p>
                        </div>
                    )}
                    
                    {activeTab === 'logs' && (
                        <div className="coming-soon">
                            <h2>ğŸ“‹ Logs d'administration</h2>
                            <p>Cette fonctionnalitÃ© sera disponible prochainement</p>
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

// Composant pour les statistiques du tableau de bord
const DashboardStats = ({ stats }) => {
    return (
        <div className="dashboard-stats">
            <h2>ğŸ“Š Statistiques de la plateforme</h2>
            
            <div className="stats-grid">
                <div className="stat-card">
                    <div className="stat-icon">ğŸ‘¥</div>
                    <div className="stat-content">
                        <h3>{stats.totalUsers || 0}</h3>
                        <p>Utilisateurs total</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ‘¨â€ğŸ‘©â€ğŸ‘§â€ğŸ‘¦</div>
                    <div className="stat-content">
                        <h3>{stats.totalParents || 0}</h3>
                        <p>Parents</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ§’</div>
                    <div className="stat-content">
                        <h3>{stats.totalChildren || 0}</h3>
                        <p>Enfants</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ‘¨â€ğŸ«</div>
                    <div className="stat-content">
                        <h3>{stats.totalTeachers || 0}</h3>
                        <p>Enseignants</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ“</div>
                    <div className="stat-content">
                        <h3>{stats.totalPosts || 0}</h3>
                        <p>Publications</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ®</div>
                    <div className="stat-content">
                        <h3>{stats.totalGames || 0}</h3>
                        <p>Mini-jeux</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">âœ¨</div>
                    <div className="stat-content">
                        <h3>{stats.newUsersToday || 0}</h3>
                        <p>Nouveaux aujourd'hui</p>
                    </div>
                </div>
                
                <div className="stat-card">
                    <div className="stat-icon">ğŸ“ˆ</div>
                    <div className="stat-content">
                        <h3>{stats.activeUsersLast7Days || 0}</h3>
                        <p>Actifs (7 jours)</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Composant pour la gestion des utilisateurs
const UsersManagement = ({ users, onRefresh }) => {
    const [selectedUser, setSelectedUser] = useState(null);
    const [showUserModal, setShowUserModal] = useState(false);
    const [actionLoading, setActionLoading] = useState(false);

    const handleUserAction = async (userId, action, data = {}) => {
        try {
            setActionLoading(true);
            
            const response = await fetch(`/api/admin/users/${userId}/${action}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify(data)
            });
            
            if (response.ok) {
                await onRefresh();
                setShowUserModal(false);
            } else {
                const error = await response.json();
                alert(`Erreur: ${error.error || 'Action Ã©chouÃ©e'}`);
            }
        } catch (error) {
            console.error('Erreur action utilisateur:', error);
            alert('Erreur lors de l\'action');
        } finally {
            setActionLoading(false);
        }
    };

    const openUserModal = (user) => {
        setSelectedUser(user);
        setShowUserModal(true);
    };

    return (
        <div className="users-management">
            <h2>ğŸ‘¥ Gestion des utilisateurs</h2>
            
            <div className="users-table">
                <div className="table-header">
                    <div>Nom</div>
                    <div>Email</div>
                    <div>Type</div>
                    <div>Niveau</div>
                    <div>Actions</div>
                </div>
                
                {users.map(user => (
                    <div key={user.id} className="table-row">
                        <div className="user-name">
                            <img 
                                src={user.profile_picture || '/default-avatar.png'} 
                                alt="Avatar"
                                className="user-avatar"
                            />
                            <span>{user.name}</span>
                        </div>
                        <div>{user.email}</div>
                        <div>
                            <span className={`user-type ${user.account_type}`}>
                                {user.account_type}
                            </span>
                        </div>
                        <div>Niveau {user.level}</div>
                        <div>
                            <button 
                                onClick={() => openUserModal(user)}
                                className="action-btn view-btn"
                            >
                                ğŸ‘ï¸ Voir
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {showUserModal && selectedUser && (
                <UserModal 
                    user={selectedUser}
                    onClose={() => setShowUserModal(false)}
                    onAction={handleUserAction}
                    loading={actionLoading}
                />
            )}
        </div>
    );
};

// Modal pour les actions utilisateur
const UserModal = ({ user, onClose, onAction, loading }) => {
    const [newPassword, setNewPassword] = useState('');
    const [showPasswordReset, setShowPasswordReset] = useState(false);

    return (
        <div className="modal-overlay">
            <div className="user-modal">
                <div className="modal-header">
                    <h3>ğŸ‘¤ {user.name}</h3>
                    <button onClick={onClose} className="close-btn">Ã—</button>
                </div>
                
                <div className="modal-content">
                    <div className="user-info">
                        <p><strong>Email:</strong> {user.email}</p>
                        <p><strong>Type:</strong> {user.account_type}</p>
                        <p><strong>Niveau:</strong> {user.level}</p>
                        <p><strong>Fragments:</strong> {user.fragments}</p>
                        <p><strong>CrÃ©Ã© le:</strong> {new Date(user.created_at).toLocaleDateString()}</p>
                    </div>
                    
                    <div className="modal-actions">
                        <button 
                            onClick={() => onAction(user.id, 'toggle-status', { 
                                action: user.is_suspended ? 'activate' : 'suspend',
                                reason: 'Action admin'
                            })}
                            className={`action-btn ${user.is_suspended ? 'activate-btn' : 'suspend-btn'}`}
                            disabled={loading}
                        >
                            {user.is_suspended ? 'âœ… RÃ©activer' : 'ğŸš« Suspendre'}
                        </button>
                        
                        <button 
                            onClick={() => setShowPasswordReset(!showPasswordReset)}
                            className="action-btn reset-btn"
                        >
                            ğŸ”‘ RÃ©initialiser mot de passe
                        </button>
                        
                        {user.account_type !== 'admin' && (
                            <button 
                                onClick={() => onAction(user.id, 'promote')}
                                className="action-btn promote-btn"
                                disabled={loading}
                            >
                                â¬†ï¸ Promouvoir Admin
                            </button>
                        )}
                    </div>
                    
                    {showPasswordReset && (
                        <div className="password-reset">
                            <input
                                type="password"
                                placeholder="Nouveau mot de passe"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                            />
                            <button
                                onClick={() => {
                                    if (newPassword.length >= 6) {
                                        onAction(user.id, 'reset-password', { newPassword });
                                        setNewPassword('');
                                        setShowPasswordReset(false);
                                    }
                                }}
                                className="action-btn confirm-btn"
                                disabled={loading || newPassword.length < 6}
                            >
                                Confirmer
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AdminDashboard;
