import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './QuizGame.css';
import { useAuth } from '../../context/AuthContext';

const QuizGame = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [showScore, setShowScore] = useState(false);
    const { token } = useAuth(); // Get token from auth context

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/quizzes', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setQuestions(response.data);
            } catch (error) {
                console.error('Error fetching questions:', error);
            }
        };
        
        if (token) {
            fetchQuestions();
        }
    }, [token]);

    const handleAnswerButtonClick = (isCorrect) => {
        if (isCorrect) {
            setScore(score + 1);
        }

        const nextQuestion = currentQuestionIndex + 1;
        if (nextQuestion < questions.length) {
            setCurrentQuestionIndex(nextQuestion);
        } else {
            setShowScore(true);
        }
    };

    return (
        <div className='quiz-game'>
            {showScore ? (
                <div className='score-section'>
                    You scored {score} out of {questions.length}
                </div>
            ) : (
                <>
                    {questions.length > 0 && (
                        <div className='question-section'>
                            <div className='question-count'>
                                <span>Question {currentQuestionIndex + 1}</span>/{questions.length}
                            </div>
                            <div className='question-text'>{questions[currentQuestionIndex].question}</div>
                        </div>
                    )}
                    <div className='answer-section'>
                        {questions.length > 0 && questions[currentQuestionIndex].options.map((option, index) => (
                            <button key={index} onClick={() => handleAnswerButtonClick(option.isCorrect)}>
                                {option.text}
                            </button>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default QuizGame;
