import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import BadgeDisplay from '../components/BadgeDisplay';
import AchievementDisplay from '../components/AchievementDisplay';
import XPProgress from '../components/XPProgress';
import LeaderboardWidget from '../components/LeaderboardWidget';
import '../styles/GamificationDashboard.css';

const GamificationDashboard = () => {
    const { user, token } = useContext(AuthContext);
    const [userProgress, setUserProgress] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('overview');

    useEffect(() => {
        fetchUserProgress();
    }, []);

    const fetchUserProgress = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/gamification/progress', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (response.ok) {
                const data = await response.json();
                setUserProgress(data.data);
            } else {
                setError('Failed to load gamification progress');
            }
        } catch (error) {
            console.error('Error fetching user progress:', error);
            setError('Failed to connect to server');
        } finally {
            setLoading(false);
        }
    };

    const renderOverview = () => {
        if (!userProgress) return null;

        return (
            <div className="overview-content">
                <div className="stats-grid">
                    <div className="stat-card xp-card">
                        <XPProgress 
                            currentXP={userProgress.xp.total}
                            currentLevel={userProgress.xp.currentLevel}
                            xpToNextLevel={userProgress.xp.xpToNextLevel}
                            levelProgress={userProgress.xp.levelProgress}
                            showDetails={true}
                        />
                    </div>

                    <div className="stat-card badges-summary">
                        <h3>üèÖ Badges</h3>
                        <div className="badges-stats">
                            <div className="total-badges">
                                <span className="number">{userProgress.badges.total}</span>
                                <span className="label">Total Badges</span>
                            </div>
                            <div className="badges-breakdown">
                                <div className="rarity-stat">
                                    <span className="common">Common: {userProgress.badges.byRarity.common}</span>
                                </div>
                                <div className="rarity-stat">
                                    <span className="rare">Rare: {userProgress.badges.byRarity.rare}</span>
                                </div>
                                <div className="rarity-stat">
                                    <span className="epic">Epic: {userProgress.badges.byRarity.epic}</span>
                                </div>
                                <div className="rarity-stat">
                                    <span className="legendary">Legendary: {userProgress.badges.byRarity.legendary}</span>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="stat-card achievements-summary">
                        <h3>üèÜ Achievements</h3>
                        <div className="achievements-stats">
                            <div className="completed-achievements">
                                <span className="number">{userProgress.achievements.stats?.completed || 0}</span>
                                <span className="label">Completed</span>
                            </div>
                            <div className="in-progress">
                                <span className="number">{userProgress.achievements.stats?.inProgress || 0}</span>
                                <span className="label">In Progress</span>
                            </div>
                        </div>
                    </div>

                    <div className="stat-card streaks-summary">
                        <h3>üî• Streaks</h3>
                        <div className="streaks-stats">
                            <div className="current-streak">
                                <span className="number">{userProgress.streaks?.daily?.current || 0}</span>
                                <span className="label">Daily Streak</span>
                            </div>
                            <div className="longest-streak">
                                <span className="number">{userProgress.streaks?.daily?.longest || 0}</span>
                                <span className="label">Best Streak</span>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="recent-activity">
                    <h3>Recent Activity</h3>
                    <div className="activity-grid">
                        <div className="recent-badges">
                            <h4>Latest Badges</h4>
                            <div className="badges-list">
                                {userProgress.badges.recent?.slice(0, 5).map((userBadge, index) => (
                                    <BadgeDisplay
                                        key={index}
                                        badge={userBadge.badge}
                                        userBadge={userBadge}
                                        earned={true}
                                        size="small"
                                        showDetails={false}
                                    />
                                ))}
                            </div>
                        </div>

                        <div className="recent-achievements">
                            <h4>Recent Achievements</h4>
                            <div className="achievements-list">
                                {userProgress.achievements.recent?.slice(0, 3).map((achievement, index) => (
                                    <AchievementDisplay
                                        key={index}
                                        achievement={achievement}
                                        compact={true}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    };

    if (loading) {
        return (
            <div className="gamification-dashboard">
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Loading your progress...</p>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="gamification-dashboard">
                <div className="error-container">
                    <h2>‚ö†Ô∏è Error</h2>
                    <p>{error}</p>
                    <button onClick={fetchUserProgress} className="retry-btn">
                        Try Again
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="gamification-dashboard">
            <div className="dashboard-header">
                <div className="user-info">
                    <h1>Welcome back, {user?.name}! üéâ</h1>
                    <p>Track your learning progress and achievements</p>
                </div>
                <div className="level-badge">
                    <span className="level-text">Level {userProgress?.xp?.currentLevel || 1}</span>
                </div>
            </div>

            <div className="dashboard-tabs">
                <button 
                    className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
                    onClick={() => setActiveTab('overview')}
                >
                    üìä Overview
                </button>
                <button 
                    className={`tab-btn ${activeTab === 'badges' ? 'active' : ''}`}
                    onClick={() => setActiveTab('badges')}
                >
                    üèÖ Badges
                </button>
                <button 
                    className={`tab-btn ${activeTab === 'achievements' ? 'active' : ''}`}
                    onClick={() => setActiveTab('achievements')}
                >
                    üèÜ Achievements
                </button>
                <button 
                    className={`tab-btn ${activeTab === 'leaderboards' ? 'active' : ''}`}
                    onClick={() => setActiveTab('leaderboards')}
                >
                    ü•á Leaderboards
                </button>
            </div>

            <div className="dashboard-content">
                {activeTab === 'overview' && renderOverview()}
                {activeTab === 'badges' && (
                    <BadgeDisplay 
                        showAllBadges={true} 
                        token={token}
                    />
                )}
                {activeTab === 'achievements' && (
                    <AchievementDisplay 
                        showAllAchievements={true} 
                        token={token}
                    />
                )}
                {activeTab === 'leaderboards' && (
                    <LeaderboardWidget 
                        showAllLeaderboards={true} 
                        token={token}
                    />
                )}
            </div>
        </div>
    );
};

export default GamificationDashboard;
