/**
 * @file config.js
 * @description Configuration file for application-wide settings and environment variables
 */

// Use environment variables or fallback to default values
export const BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
export const API_BASE_URL = `${BASE_URL}/api`;

// Authentication settings
export const AUTH = {
  TOKEN_KEY: 'token',
  USER_KEY: 'user',
  TOKEN_EXPIRY_DAYS: 7,
};

// Feature flags
export const FEATURES = {
  ENABLE_QUIZ_GAME: true,
  ENABLE_SOCIAL_FEATURES: true,
  ENABLE_CHAT: true,
  ENABLE_ANALYTICS: process.env.NODE_ENV === 'production',
  DEBUG_MODE: process.env.NODE_ENV !== 'production',
};

// App-wide settings
export const SETTINGS = {
  PAGINATION_LIMIT: 10,
  MAX_FILE_SIZE_MB: 5,
  SUPPORTED_FILE_TYPES: ['image/jpeg', 'image/png', 'image/gif'],
  UPLOAD_PATH: `${BASE_URL}/uploads/`,
};

// Validate the configuration
if (!BASE_URL) {
  console.warn('API URL not properly configured! This might cause connection issues.');
}

export default {
  BASE_URL,
  API_BASE_URL,
  AUTH,
  FEATURES,
  SETTINGS,
};
