import React from 'react';
import '../styles/XPProgress.css';

const XPProgress = ({ 
    currentXP, 
    currentLevel, 
    xpToNextLevel, 
    levelProgress, 
    showDetails = false,
    showHistory = false,
    recentHistory = []
}) => {
    const calculateLevelXP = (level) => {
        // Simple XP calculation - you can adjust this formula
        return level * 100 + (level - 1) * 50;
    };

    const getCurrentLevelXP = () => {
        if (currentLevel <= 1) return 0;
        return calculateLevelXP(currentLevel - 1);
    };

    const getNextLevelXP = () => {
        return calculateLevelXP(currentLevel);
    };

    const getCurrentLevelProgress = () => {
        const currentLevelStartXP = getCurrentLevelXP();
        const nextLevelXP = getNextLevelXP();
        const progressInLevel = currentXP - currentLevelStartXP;
        const levelRange = nextLevelXP - currentLevelStartXP;
        
        return Math.min((progressInLevel / levelRange) * 100, 100);
    };

    const formatXP = (xp) => {
        if (xp >= 1000000) return `${(xp / 1000000).toFixed(1)}M`;
        if (xp >= 1000) return `${(xp / 1000).toFixed(1)}K`;
        return xp.toString();
    };

    const getLevelColor = (level) => {
        if (level >= 50) return '#ff9800'; // Orange for high levels
        if (level >= 25) return '#9c27b0'; // Purple for medium-high levels
        if (level >= 10) return '#2196f3'; // Blue for medium levels
        if (level >= 5) return '#4caf50';  // Green for early levels
        return '#757575'; // Gray for beginners
    };

    return (
        <div className="xp-progress-container">
            <div className="xp-header">
                <div className="level-display">
                    <div 
                        className="level-circle"
                        style={{ backgroundColor: getLevelColor(currentLevel) }}
                    >
                        <span className="level-number">{currentLevel}</span>
                    </div>
                    <div className="level-info">
                        <h3 className="level-title">Level {currentLevel}</h3>
                        {showDetails && (
                            <p className="xp-total">{formatXP(currentXP)} XP Total</p>
                        )}
                    </div>
                </div>

                {showDetails && (
                    <div className="xp-stats">
                        <div className="stat-item">
                            <span className="stat-value">{formatXP(currentXP)}</span>
                            <span className="stat-label">Total XP</span>
                        </div>
                        <div className="stat-item">
                            <span className="stat-value">{formatXP(xpToNextLevel || 0)}</span>
                            <span className="stat-label">To Next Level</span>
                        </div>
                    </div>
                )}
            </div>

            <div className="progress-section">
                <div className="progress-info">
                    <span className="progress-text">
                        {formatXP(currentXP - getCurrentLevelXP())} / {formatXP(getNextLevelXP() - getCurrentLevelXP())} XP
                    </span>
                    <span className="progress-percentage">
                        {Math.round(levelProgress || getCurrentLevelProgress())}%
                    </span>
                </div>
                <div className="progress-bar">
                    <div 
                        className="progress-fill"
                        style={{ 
                            width: `${levelProgress || getCurrentLevelProgress()}%`,
                            backgroundColor: getLevelColor(currentLevel)
                        }}
                    >
                        <div className="progress-shine"></div>
                    </div>
                </div>
                <div className="level-markers">
                    <span className="current-level">Lv. {currentLevel}</span>
                    <span className="next-level">Lv. {currentLevel + 1}</span>
                </div>
            </div>

            {showHistory && recentHistory && recentHistory.length > 0 && (
                <div className="xp-history">
                    <h4>Recent XP Gains</h4>
                    <div className="history-list">
                        {recentHistory.slice(0, 5).map((entry, index) => (
                            <div key={index} className="history-item">
                                <div className="history-info">
                                    <span className="history-source">{entry.source}</span>
                                    <span className="history-description">{entry.description}</span>
                                </div>
                                <div className="history-xp">
                                    <span className="xp-amount">+{entry.amount}</span>
                                    <span className="xp-date">
                                        {new Date(entry.timestamp).toLocaleDateString()}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {showDetails && (
                <div className="level-benefits">
                    <h4>Level Benefits</h4>
                    <div className="benefits-list">
                        <div className="benefit-item">
                            <span className="benefit-icon">‚ö°</span>
                            <span className="benefit-text">XP Multiplier: {(1 + (currentLevel - 1) * 0.05).toFixed(2)}x</span>
                        </div>
                        {currentLevel >= 5 && (
                            <div className="benefit-item">
                                <span className="benefit-icon">üèÖ</span>
                                <span className="benefit-text">Badge Bonus Unlocked</span>
                            </div>
                        )}
                        {currentLevel >= 10 && (
                            <div className="benefit-item">
                                <span className="benefit-icon">üî•</span>
                                <span className="benefit-text">Streak Bonus Enhanced</span>
                            </div>
                        )}
                        {currentLevel >= 25 && (
                            <div className="benefit-item">
                                <span className="benefit-icon">üëë</span>
                                <span className="benefit-text">Elite Status Achieved</span>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default XPProgress;
