import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import {
  FaGamepad, FaStar, FaTrophy, FaBolt, FaPlay, 
  FaChartLine, FaRegLightbulb, FaCrown, FaArrowLeft
} from 'react-icons/fa';
import 'src/styles/MiniGames.css';

const MiniGames = () => {
    const [games, setGames] = useState([]);
    const [userProgress, setUserProgress] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [hoveredCard, setHoveredCard] = useState(null);
    
    const { token } = useAuth();
    const navigate = useNavigate();
    
    useEffect(() => {
        const fetchGames = async () => {
            try {
                setLoading(true);
                
                // Fetch available games
                const response = await axios.get('http://localhost:5000/api/games/available', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                
                setGames(response.data.games || []);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching games:', err);
                setError('Erreur lors du chargement des mini-jeux. Veuillez réessayer.');
                setLoading(false);
            }
        };
        
        fetchGames();
    }, [token]);
    
    const handlePlayGame = (gameId) => {
        navigate(`/jeu/${gameId}`);
    };
    
    const handleBackToHome = () => {
        navigate('/');
    };
    
    if (loading) {
        return (
            <div className="minigames-page">
                <div className="minigames-container">
                    <div className="loading-state">
                        <div className="loading-spinner"></div>
                        <p>Chargement des mini-jeux...</p>
                    </div>
                </div>
            </div>
        );
    }
    
    if (error) {
        return (
            <div className="minigames-page">
                <div className="minigames-container">
                    <div className="error-state">
                        <h2>Une erreur est survenue</h2>
                        <p>{error}</p>
                        <button onClick={() => window.location.reload()} className="btn-retry">
                            Réessayer
                        </button>
                    </div>
                </div>
            </div>
        );
    }
    
    return (
        <div className="minigames-page">
            <div className="minigames-container">
                <button className="back-button" onClick={handleBackToHome}>
                    <FaArrowLeft /> Retour à l'accueil
                </button>
                
                <div className="minigames-hero">
                    <div className="minigames-hero-content">
                        <h1><FaGamepad /> Mini-Jeux Éducatifs</h1>
                        <p>Choisis un mini-jeu pour tester tes connaissances et gagner de l'XP!</p>
                    </div>
                </div>
                
                <div className="minigames-grid">
                    {games.length > 0 ? (
                        games.map(game => (
                            <div 
                                key={game._id}
                                className="minigames-card"
                                onMouseEnter={() => setHoveredCard(game._id)}
                                onMouseLeave={() => setHoveredCard(null)}
                            >
                                <div className="card-glow" 
                                    style={{opacity: hoveredCard === game._id ? 1 : 0}}></div>
                                
                                <div className="minigames-card-img">
                                    <img src={game.imageUrl || '/assets/default-game.png'} alt={game.name} />
                                </div>
                                
                                <h3>{game.name}</h3>
                                <div className="minigames-card-subject">{game.subject}</div>
                                <p>{game.description}</p>
                                
                                <div className="minigames-card-stats">
                                    <div className="minigames-stat">
                                        <strong>Difficulté</strong>
                                        <div className="stat-value">
                                            <span>{game.difficulty}</span>
                                        </div>
                                    </div>
                                    <div className="minigames-stat">
                                        <strong>Récompense</strong>
                                        <div className="stat-value">
                                            <span>{game.xpReward} XP</span>
                                        </div>
                                    </div>
                                </div>
                                
                                <button 
                                    onClick={() => handlePlayGame(game._id)}
                                    className="minigames-play-btn"
                                >
                                    <FaPlay />
                                    <span>Jouer</span>
                                </button>
                            </div>
                        ))
                    ) : (
                        <div className="no-games-message">
                            <h3>Aucun mini-jeu disponible pour le moment</h3>
                            <p>Reviens bientôt pour découvrir nos nouveaux jeux éducatifs!</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default MiniGames;
