import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import axios from 'axios';
import { 
  Clock, Trophy, Star, Plus, TrendingUp, Settings, Shield, Bell, 
  Target, Users, User, Mail, KeyRound, Zap
} from 'lucide-react';
import './ParentDashboard.css';

const StatCard = ({ icon: Icon, title, value, subtitle, trend }) => (
    <div className="stat-card">
        <div className="stat-icon">
            <Icon size={32} />
        </div>
        <div className="stat-info">
            <h3>{title}</h3>
            <p className="stat-value">{value}</p>
            {subtitle && <span className="stat-period">{subtitle}</span>}
        </div>
        {trend && <span className="stat-trend positive">+{trend}%</span>}
    </div>
);

const OverviewTab = ({ selectedChild }) => {
    if (!selectedChild) return null;

    return (
        <div className="overview-tab">
            <div className="welcome-banner">
                <div className="welcome-banner-content">
                    <div className="welcome-avatar">{selectedChild.avatar || 'ðŸ‘§'}</div>
                    <div>
                        <h2 className="welcome-title">Bonjour, {selectedChild.name}!</h2>
                        <p className="welcome-subtitle">Niveau {selectedChild.level} â€¢ {selectedChild.rank || 'DÃ©butant'}</p>
                    </div>
                    <div className="welcome-streak">
                        <Zap size={16} />
                        <span>{selectedChild.streak || 0} jours de suite</span>
                    </div>
                </div>
                <p className="welcome-message">
                    Continue comme Ã§a ! Tu progresses vraiment bien en {selectedChild.favorite_subject || 'gÃ©nÃ©ral'}.
                </p>
            </div>

            <div className="stats-grid">
                <StatCard
                    icon={Clock}
                    title="Temps d'apprentissage"
                    value="2h 30m"
                    subtitle="Cette semaine"
                    trend="15"
                />
                <StatCard
                    icon={Trophy}
                    title="QuÃªtes complÃ©tÃ©es"
                    value={selectedChild.quests_completed || 0}
                    subtitle="Total"
                    trend="8"
                />
                <StatCard
                    icon={Star}
                    title="Fragments"
                    value={selectedChild.fragments || 0}
                    subtitle="CollectÃ©s"
                    trend="12"
                />
                <StatCard
                    icon={Target}
                    title="Objectif du jour"
                    value="3/4"
                    subtitle="QuÃªtes complÃ©tÃ©es"
                />
            </div>

            <div className="recent-activity">
                <h3>ActivitÃ© rÃ©cente</h3>
                <div className="activity-list">
                    {/*
                        { icon: "ðŸŽ¯", title: "QuÃªte "MathÃ©matiques" complÃ©tÃ©e", time: "Il y a 2 heures", reward: "+10 fragments" },
                        { icon: "ðŸ“š", title: "Nouveau niveau atteint en FranÃ§ais", time: "Hier", reward: "Niveau 3" },
                        { icon: "ðŸ†", title: "Badge "Explorateur" dÃ©bloquÃ©", time: "Il y a 3 jours", reward: "Nouveau badge" }
                    */}
                    {Array.from({ length: 3 }).map((_, index) => (
                        <div key={index} className="activity-item">
                            <div className="activity-icon">ðŸŽ¯</div>
                            <div className="activity-details">
                                <p><strong>QuÃªte "MathÃ©matiques" complÃ©tÃ©e</strong></p>
                                <span>Il y a 2 heures</span>
                            </div>
                            <div className="activity-reward">+10 fragments</div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

const ProgressTab = () => {
    return (
        <div className="progress-tab">
            <div className="subject-progress">
                <h3>Progression par matiÃ¨re</h3>
                <div className="subjects-grid">
                    {/*
                        { name: 'MathÃ©matiques', progress: 78, level: 4, quests: 12, icon: 'ðŸ”¢' },
                        { name: 'FranÃ§ais', progress: 65, level: 3, quests: 8, icon: 'ðŸ“' },
                        { name: 'Sciences', progress: 82, level: 5, quests: 15, icon: 'ðŸ”¬' },
                        { name: 'Histoire', progress: 45, level: 2, quests: 6, icon: 'ðŸ›ï¸' },
                        { name: 'Anglais', progress: 58, level: 3, quests: 9, icon: 'ðŸ‡¬ðŸ‡§' },
                        { name: 'Arts', progress: 73, level: 4, quests: 11, icon: 'ðŸŽ¨' }
                    */}
                    {Array.from({ length: 6 }).map((_, index) => (
                        <div key={index} className="subject-card">
                            <div className="subject-card-header">
                                <span className="subject-icon">ðŸ”¢</span>
                                <h4>MathÃ©matiques</h4>
                            </div>
                            <div className="progress-bar">
                                <div className="progress-fill" style={{ width: `75%` }}></div>
                            </div>
                            <div className="subject-stats">
                                <span>Niveau 4</span>
                                <span>12 quÃªtes</span>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            <div className="weekly-chart">
                <h3>ActivitÃ© de la semaine</h3>
                <div className="chart-placeholder">
                    <div className="chart-bars">
                        {['Lun', 'Mar', 'Mer', 'Jeu', 'Ven', 'Sam', 'Dim'].map((day) => {
                            const height = Math.random() * 100;
                            return (
                                <div key={day} className="chart-bar">
                                    <div className="bar-fill" style={{ height: `${height}%` }}></div>
                                    <span>{day}</span>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        </div>
    );
};

const ActivityTab = () => {
    return (
        <div className="settings-tab">
            <div className="settings-section">
                <h3><Shield size={20} /> ContrÃ´les parentaux</h3>
                <div className="settings-options">
                    <div className="setting-item">
                        <div className="setting-info">
                            <h4>Temps d'Ã©cran quotidien</h4>
                            <p>Limite de temps pour l'utilisation de l'application.</p>
                        </div>
                        <div className="time-controls">
                            {['30 min', '1h', '2h', 'IllimitÃ©'].map((time, index) => (
                                <button key={time} className={`control-btn ${index === 1 ? 'active' : ''}`}>{time}</button>
                            ))}
                        </div>
                    </div>
                    <div className="setting-item">
                        <div className="setting-info">
                            <h4>Plages horaires autorisÃ©es</h4>
                            <p>DÃ©finir quand l'application peut Ãªtre utilisÃ©e.</p>
                        </div>
                        <div className="toggle-controls">
                            {/*
                                { label: 'Matin: 9h00 - 12h00', enabled: true },
                                { label: 'AprÃ¨s-midi: 14h00 - 17h00', enabled: true },
                                { label: 'Soir: 18h00 - 20h00', enabled: false }
                            */}
                            {Array.from({ length: 3 }).map((_, index) => (
                                <div key={index} className="toggle-item">
                                    <span>Matin: 9h00 - 12h00</span>
                                    <button className={`toggle-btn ${true ? 'active' : ''}`}>
                                        {true ? 'âœ“' : 'â—‹'}
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

const SettingsTab = ({ selectedChild }) => {
    if (!selectedChild) return null;
    return (
        <div className="settings-tab">
            <div className="settings-section">
                <h3><User size={20} /> Gestion du compte enfant</h3>
                <div className="settings-options">
                    <div className="setting-item">
                        <div className="setting-info">
                            <h4><User size={16} /> Nom d'utilisateur</h4>
                            <p>{selectedChild?.username}</p>
                        </div>
                        <button className="setting-btn">Modifier</button>
                    </div>
                    <div className="setting-item">
                        <div className="setting-info">
                            <h4><Mail size={16} /> Email</h4>
                            <p>{selectedChild?.email}</p>
                        </div>
                        <button className="setting-btn">Modifier</button>
                    </div>
                    <div className="setting-item">
                        <div className="setting-info">
                            <h4><KeyRound size={16} /> Mot de passe</h4>
                            <p>â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢</p>
                        </div>
                        <button className="setting-btn">Changer</button>
                    </div>
                </div>
            </div>
            <div className="settings-section">
                <h3><Bell size={20} /> Notifications</h3>
                <div className="notification-settings">
                    {/*
                        { label: 'Rapport quotidien d'activitÃ©', enabled: true },
                        { label: 'Nouveaux accomplissements', enabled: true },
                        { label: 'Rappels de temps d'Ã©cran', enabled: false }
                    */}
                    {Array.from({ length: 3 }).map((_, index) => (
                        <div key={index} className="notification-item">
                            <span>Rapport quotidien d'activitÃ©</span>
                            <button className={`toggle-btn ${true ? 'active' : ''}`}>
                                {true ? 'âœ“' : 'â—‹'}
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};


function ParentDashboard() {
    const [children, setChildren] = useState([]);
    const [selectedChild, setSelectedChild] = useState(null);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState('overview');

    const { token, user } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            navigate('/login');
            return;
        }
        fetchChildren();
    }, [user, navigate]);

    const fetchChildren = async () => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:5000/api/users/parent/children', {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            if (response.data.length > 0) {
                const childrenWithMockData = response.data.map((child, index) => ({
                    ...child,
                    avatar: index % 2 === 0 ? 'ðŸ‘§' : 'ðŸ‘¦',
                    streak: Math.floor(Math.random() * 10),
                    favorite_subject: ['MathÃ©matiques', 'Sciences', 'FranÃ§ais'][index % 3],
                }));
                setChildren(childrenWithMockData);
                setSelectedChild(childrenWithMockData[0]);
            }
        } catch (error) {
            console.error("Erreur lors de la rÃ©cupÃ©ration des enfants:", error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div className="loading-container">Chargement du tableau de bord...</div>;
    }

    if (children.length === 0) {
        return (
            <main className="page-content parent-dashboard empty">
                <div className="empty-state">
                    <div className="empty-icon"><Users size={64} /></div>
                    <h2>Aucun enfant n'est encore ajoutÃ©</h2>
                    <p>Commencez par ajouter un profil pour votre enfant pour suivre ses progrÃ¨s.</p>
                    <button className="add-child-btn" onClick={() => navigate('/parent-setup')}>
                        <Plus size={18} /> Ajouter un enfant
                    </button>
                </div>
            </main>
        );
    }

    return (
        <main className="page-content parent-dashboard">
            <div className="dashboard-header">
                <div>
                    <h1>Tableau de bord parent</h1>
                    <p className="dashboard-subtitle">Suivez les progrÃ¨s et gÃ©rez l'activitÃ© de vos enfants</p>
                </div>
                <button className="add-child-btn" onClick={() => navigate('/parent-setup')}>
                    <Plus size={18} /> Ajouter un enfant
                </button>
            </div>

            <div className="children-selector">
                <nav className="children-tabs">
                    {children.map((child) => (
                        <button
                            key={child.id}
                            className={`child-tab ${selectedChild?.id === child.id ? 'active' : ''}`}
                            onClick={() => setSelectedChild(child)}
                        >
                            <div className="child-avatar">{child.avatar}</div>
                            <span>{child.name}</span>
                        </button>
                    ))}
                </nav>
            </div>

            {selectedChild && (
                <section className="dashboard-content">
                    <div className="tab-navigation">
                        {/*
                            { key: 'overview', icon: TrendingUp, label: 'Vue d'ensemble' },
                            { key: 'progress', icon: Trophy, label: 'Progression' },
                            { key: 'activity', icon: Shield, label: 'ContrÃ´les' },
                            { key: 'settings', icon: Settings, label: 'ParamÃ¨tres' }
                        */}
                        {['overview', 'progress', 'activity', 'settings'].map((key) => (
                            <button
                                key={key}
                                className={`tab-btn ${activeTab === key ? 'active' : ''}`}
                                onClick={() => setActiveTab(key)}
                            >
                                {key}
                            </button>
                        ))}
                    </div>

                    <div className="tab-content">
                        {activeTab === 'overview' && <OverviewTab selectedChild={selectedChild} />}
                        {activeTab === 'progress' && <ProgressTab />}
                        {activeTab === 'activity' && <ActivityTab />}
                        {activeTab === 'settings' && <SettingsTab selectedChild={selectedChild} />}
                    </div>
                </section>
            )}
        </main>
    );
}

export default ParentDashboard;
